// TemperatureUtility.js - Una forma sencilla de compartir el estado de temperatura entre componentes

// Valor inicial
let currentTemperature = 25;

// Lista de suscriptores (callbacks) que serán notificados cuando cambie la temperatura
const subscribers = [];

// Obtener la temperatura actual
export const getTemperature = () => {
  return currentTemperature;
};

// Establecer una nueva temperatura y notificar a los suscriptores
export const setTemperature = (newTemperature) => {
  console.log("TemperatureUtility actualizando temperatura a:", newTemperature);
  console.log("Número de suscriptores:", subscribers.length);
  currentTemperature = newTemperature;
  subscribers.forEach(callback => {
    console.log("Notificando a un suscriptor");
    callback(newTemperature);
  });
};

// Suscribirse a cambios de temperatura
export const subscribeToTemperature = (callback) => {
  console.log("Nuevo suscriptor registrado");
  subscribers.push(callback);
  
  // Retornar una función para cancelar la suscripción
  return () => {
    const index = subscribers.indexOf(callback);
    if (index !== -1) {
      subscribers.splice(index, 1);
      console.log("Suscriptor eliminado, quedan:", subscribers.length);
    }
  };
};

// Exportamos un objeto para ser usado sin ESM
const TemperatureUtility = {
  getTemperature,
  setTemperature,
  subscribeToTemperature
};

export default TemperatureUtility;
