import React, { useState } from "react";
import styled from "styled-components";
import Header from "../components/Header";  
import Footer from "../components/Footer";
import InputField from "../components/InputField";
import Button from "../components/Button";
import gecko from "../assets/reptil-gecko.png";
import planta from "../assets/planta.png";

// ðŸ“Œ Contenedor principal (igual que en Home)
const Container = styled.div`
  padding-top: ${(props) => props.headerHeight + 40}px;
  padding-left: 40px;
  padding-right: 40px;
  padding-bottom: 75px;
  background: white;
  border-radius: 20px;
  width: 91%;
  max-width: 1200px;
  margin: auto;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const LoginBox = styled.div`
  background: rgb(239,191,134);
  background: linear-gradient(90deg, rgba(239,191,134,0.51) 0%, rgba(236,137,19,0.5) 51%, rgba(239,191,134,0.5) 100%);
  padding: 30px;
  border-radius: 50px;
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5),
              10px 10px 20px rgba(245, 239, 230, 0.2);
  text-align: center;
  width: 50%;
  position: relative;
`;

const Title = styled.h2`
  color: black;
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin: 10px 0 5px;
  font-weight: bold;
  color: black;
`;

const RememberMe = styled.div`
  display: flex;
  align-items: center;
  margin-top: 10px;
  color: black;
`;

const StyledGecko = styled.img`
  position: absolute;
  top: 14%;
  left: 76%;
  width: 250px;
  filter: brightness(50%);
`;

const StyledPlanta = styled.img`
  position: absolute;
  bottom: 1%;
  left: 33%;
  width: 200px;
`;

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);
  const [headerHeight, setHeaderHeight] = useState(0);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Limpiar errores previos

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Inicio de sesiÃ³n exitoso");
        localStorage.setItem("token", data.token); // Guardar token en localStorage
      } else {
        setError(data.error); // Mostrar error en pantalla
      }
    } catch (error) {
      console.error("Error al iniciar sesiÃ³n:", error);
      setError("Error de conexiÃ³n con el servidor");
    }
  };

  return (
    <>
      <Header setHeaderHeight={setHeaderHeight} hideLoginButton={true} />

      <Container headerHeight={headerHeight}>
        <StyledGecko src={gecko} alt="Gecko" />
        <StyledPlanta src={planta} alt="Planta" />

        <LoginBox>
          <Title>INICIO DE SESIÃ“N</Title>
          {error && <p style={{ color: "red" }}>{error}</p>} {/* Mostrar errores */}
          <form onSubmit={handleSubmit}>
            <Label>Usuario</Label>
            <InputField
              type="text"
              placeholder="Ingresa tu usuario"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />

            <Label>ContraseÃ±a</Label>
            <InputField
              type="password"
              placeholder="Ingresa tu contraseÃ±a"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />

            <RememberMe>
              <input
                type="checkbox"
                checked={remember}
                onChange={() => setRemember(!remember)}
              />
              <span>RecuÃ©rdame</span>
            </RememberMe>

            <Button text="INICIAR SESIÃ“N" />
          </form>
        </LoginBox>
      </Container>

      <Footer />
    </>
  );
};

export default Login;
