import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import InputField from "../components/InputField";
import Button from "../components/Button";
import gecko from "../assets/reptil-gecko.png";
import planta from "../assets/planta.png";
import {jwtDecode} from "jwt-decode";

const Container = styled.div`
  padding-top: ${(props) => props.headerHeight + 40}px;
  padding-left: 40px;
  padding-right: 40px;
  padding-bottom: 75px;
  background: white;
  border-radius: 20px;
  width: 80%;
  max-width: 500px;
  margin: auto;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const RegisterBox = styled.div`
  background: linear-gradient(90deg, rgba(239,191,134,0.51) 0%, rgba(236,137,19,0.5) 51%, rgba(239,191,134,0.5) 100%);
  padding: 30px;
  border-radius: 30px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  text-align: center;
  width: 90%;
`;

const Title = styled.h2`
  color: black;
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin: 10px 0 5px;
  font-weight: bold;
  color: black;
`;

const StyledGecko = styled.img`
  position: absolute;
  top: 14%;
  left: 76%;
  width: 250px;
  filter: brightness(50%);
`;

const StyledPlanta = styled.img`
  position: absolute;
  bottom: 1%;
  left: 33%;
  width: 200px;
`;

const Edit_User = () => {
  const [headerHeight, setHeaderHeight] = useState(0);
  const [Usuario, setUsername] = useState("");
  const [Contrasena, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState(null);
  const [currentUsername, setCurrentUsername] = useState('');
  const [currentEmail, setCurrentEmail] = useState('');
  const navigate = useNavigate();

  // Función para obtener información del usuario desde el token
  const getUserInfoFromToken = () => {
    const token = localStorage.getItem("token");
    
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setCurrentUsername(decoded.user);
        setCurrentEmail(decoded.email);
        return { username: decoded.user, email: decoded.email };
      } catch (error) {
        console.error("Error al decodificar el token:", error);
        return { username: '', email: '' };
      }
    }
    return { username: '', email: '' };
  };

  // Cargar datos del usuario al iniciar el componente
  useEffect(() => {
    const userInfo = getUserInfoFromToken();
    setCurrentUsername(userInfo.username);
    setCurrentEmail(userInfo.email);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!Usuario || !Contrasena || !confirmPassword) {
      setError("Todos los campos son obligatorios");
      return;
    }

    if (Contrasena !== confirmPassword) {
      setError("Las contraseñas no coinciden");
      return;
    }

    try {
      // Obtener el token del localStorage
      const token = localStorage.getItem("token");
      
      if (!token) {
        setError("No se ha iniciado sesión");
        return;
      }

      const response = await fetch("http://localhost:5000/edit-user", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ 
          Usuario, 
          Contrasena,
          token // Enviamos el token como parte del cuerpo de la solicitud
        }),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.error);
        return;
      }

      // Guardar el nuevo token que contiene la información actualizada
      if (data.newToken) {
        localStorage.setItem("token", data.newToken);
        
        // Actualización exitosa
        alert("Usuario actualizado correctamente. La página se recargará para aplicar los cambios.");
        
        // Solución directa: redirigir al dashboard con recarga completa de página
        window.location.href = "/dashboard";
      } else {
        // En caso de no recibir token, simplemente navegar
        alert("Usuario actualizado correctamente");
        navigate("/dashboard");
      }
    } catch (error) {
      setError("Error en la conexión con el servidor");
    }
  };

  return (
    <>
      <Header setHeaderHeight={setHeaderHeight} hideLoginButton={true} />

      <Container headerHeight={headerHeight}>
        <StyledGecko src={gecko} alt="Gecko" />
        <StyledPlanta src={planta} alt="Planta" />

        <RegisterBox>
          <Title>EDITAR DATOS {currentEmail}</Title>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <form onSubmit={handleSubmit}>
            <Label>Usuario</Label>
            <InputField
              type="text"
              placeholder={currentUsername}
              value={Usuario}
              onChange={(e) => setUsername(e.target.value)}
            />

            <Label>Contraseña</Label>
            <InputField
              type="password"
              placeholder="Ingresa una nueva contraseña"
              value={Contrasena}
              onChange={(e) => setPassword(e.target.value)}
            />

            <Label>Confirmar Contraseña</Label>
            <InputField
              type="password"
              placeholder="Confirma la nueva contraseña"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            />

            <Button text="GUARDAR CAMBIOS" type="submit" />
          </form>
        </RegisterBox>
      </Container>

      <Footer />
    </>
  );
};

export default Edit_User;
