import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// üìå Estilos Generales
const Container = styled.div`
  padding: 50px 30px 30px;
  margin: auto;
  max-width: 1200px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

// üìå Tarjetas de Informaci√≥n
const Card = styled.div`
  background: #a67c52;
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  position: relative;
`;

const CardTitle = styled.h3`
  background: #3c722b;
  padding: 10px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  text-align: center;
`;

const Value = styled.p`
  font-size: 24px;
  font-weight: bold;
`;

// üìå Panel de Control
const ControlPanel = styled(Card)`
  background: #5d4037;
  padding-top: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Slider = styled.input`
  width: 80%;
  margin: 10px 0;
`;

const SwitchContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 80%;
`;

const Switch = styled.label`
  display: flex;
  align-items: center;
  gap: 10px;
  cursor: pointer;
`;

const Checkbox = styled.input`
  transform: scale(1.5);
`;

const ControlButton = styled.button`
  background: #4caf50;
  border: none;
  color: white;
  padding: 10px;
  font-size: 14px;
  cursor: pointer;
  border-radius: 8px;
  margin-top: 10px;
`;

// üìå Estado General
const StatusPanel = styled(Card)`
  background: #3e2723;
  height: 100%;
`;

const StatusItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 18px;
  font-weight: bold;
  background: ${({ color }) => color || "gray"};
  padding: 10px;
  border-radius: 10px;
  margin-top: 10px;
`;

const StatusImage = styled.img`
  width: 25px;
  height: 25px;
`;

const Dashboard = () => {
  // Simulaci√≥n de datos para que se vean en la pantalla (puedes conectarlo al backend despu√©s)
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(50);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(false);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaPiel, setMudaPiel] = useState(false);

  const [temperatura, setTemperatura] = useState(29);
  const [intensidadUV, setIntensidadUV] = useState(0.3);
  const [humedad, setHumedad] = useState(30);
  const [cicloDia, setCicloDia] = useState("ma√±ana");

  useEffect(() => {
    const hora = new Date().getHours();
    if (hora >= 6 && hora < 12) setCicloDia("ma√±ana");
    else if (hora >= 12 && hora < 18) setCicloDia("tarde");
    else setCicloDia("noche");
  }, []);

  return (
    <Container>
      {/* üìå Columna Izquierda */}
      <div>
        <Card>
          <CardTitle>Temperatura</CardTitle>
          <p>üå°Ô∏è M√≠nima: 29¬∞C - M√°xima: 32¬∞C</p>
          <Value>{temperatura}¬∞C</Value>
        </Card>
        <Card>
          <CardTitle>Iluminaci√≥n</CardTitle>
          <p>‚òÄÔ∏è Intensidad UV</p>
          <Value>{intensidadUV}</Value>
        </Card>
        <Card>
          <CardTitle>Humedad</CardTitle>
          <p>üíß Humedad %</p>
          <Value>{humedad}%</Value>
        </Card>
      </div>

      {/* üìå Panel de Control */}
      <ControlPanel>
        <CardTitle>Panel de Control</CardTitle>
        <p>Modo Manual | üîÑ Modo Autom√°tico</p>
        <Slider type="range" min="0" max="100" value={placaTermica} onChange={(e) => setPlacaTermica(e.target.value)} />
        <SwitchContainer>
          <Switch>
            <span>üí° Visible</span>
            <Checkbox type="checkbox" checked={luzVisible} onChange={() => setLuzVisible(!luzVisible)} />
          </Switch>
          <Switch>
            <span>UV</span>
            <Checkbox type="checkbox" checked={luzUV} onChange={() => setLuzUV(!luzUV)} />
          </Switch>
        </SwitchContainer>
        <ControlButton onClick={() => setHumidificador(!humidificador)}>ACTIVAR</ControlButton>
        <Switch>
          <span>Muda de Piel</span>
          <Checkbox type="checkbox" checked={mudaPiel} onChange={() => setMudaPiel(!mudaPiel)} />
        </Switch>
      </ControlPanel>

      {/* üìå Estado General */}
      <StatusPanel>
        <CardTitle>Estado General</CardTitle>
        <p>
          <StatusImage src="/icons/sun.png" /> Ciclo del D√≠a: {cicloDia}
        </p>
        <StatusItem color="green">
          <StatusImage src="/icons/check.png" /> Temperatura: Todo en orden
        </StatusItem>
        <StatusItem color="yellow">
          <StatusImage src="/icons/warning.png" /> Iluminaci√≥n: Necesita ajuste
        </StatusItem>
        <StatusItem color="red">
          <StatusImage src="/icons/error.png" /> Humedad: Necesita atenci√≥n
        </StatusItem>
      </StatusPanel>
    </Container>
  );
};

export default Dashboard;
