import React, { useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Estilos del Contenedor Principal (Diseño Grid mejorado)
const Container = styled.div`
  padding: 30px 40px;
  margin: auto;
  max-width: 1400px;
  background: #F8F4E1;  /* 🔹 Color de fondo más claro */
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 25px;
  min-height: 85vh;
`;

// 📌 Tarjetas de Información
const Card = styled.div`
  background: #C9A66B; /* 🔹 Color similar a la referencia */
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  width: 100%;
`;

const CardTitle = styled.h3`
  background: #3C722B; /* 🔹 Verde oscuro */
  padding: 10px;
  border-radius: 10px;
  margin-bottom: 15px;
  font-size: 18px;
`;

// 📌 Columnas de la cuadrícula
const LeftColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const RightColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

// 📌 Panel de Control y Estado General
const ControlPanel = styled(Card)`
  background: #5D4037;
`;

const StatusPanel = styled(Card)`
  background: #3E2723;
`;

// 📌 Estilos para la información en las tarjetas
const Info = styled.p`
  font-size: 22px;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
`;

// 📌 Contenedor para las gráficas con diseño mejorado
const ChartContainer = styled.div`
  background: white; /* 🔹 Fondo blanco para parecerse más al diseño */
  padding: 15px;
  border-radius: 15px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  width: 100%;
  height: 250px;  /* ✅ Se limita la altura */
  display: flex;
  justify-content: center;
  align-items: center;
`;

// 📌 Botón de Control
const ControlButton = styled.button`
  background: #4CAF50;
  border: none;
  color: white;
  padding: 12px;
  font-size: 16px;
  cursor: pointer;
  border-radius: 8px;
`;

const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }
  }, [navigate]);

  // 📌 Datos de las gráficas
  const temperatureData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Temperatura", data: [27, 28, 29, 30, 29, 28], borderColor: "#FF9800", backgroundColor: "rgba(255, 152, 0, 0.5)" }]
  };

  const illuminationData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Intensidad UV", data: [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], borderColor: "#FFC107", backgroundColor: "rgba(255, 193, 7, 0.5)" }]
  };

  const humidityData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Humedad %", data: [30, 35, 40, 38, 32, 30], borderColor: "#2196F3", backgroundColor: "rgba(33, 150, 243, 0.5)" }]
  };

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        {/* 📌 Columna Izquierda */}
        <LeftColumn>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <Info>🌡️ Mínima: 29°C - Máxima: 32°C</Info>
          </Card>

          <Card>
            <CardTitle>Iluminación</CardTitle>
            <Info>☀️ Intensidad UV: 0.3</Info>
          </Card>

          <Card>
            <CardTitle>Humedad</CardTitle>
            <Info>💧 Humedad: 30%</Info>
          </Card>
        </LeftColumn>

        {/* 📌 Columna Central */}
        <CenterColumn>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <ChartContainer>
              <Line data={temperatureData} />
            </ChartContainer>
          </Card>

          <Card>
            <CardTitle>Iluminación</CardTitle>
            <ChartContainer>
              <Line data={illuminationData} />
            </ChartContainer>
          </Card>

          <Card>
            <CardTitle>Humedad</CardTitle>
            <ChartContainer>
              <Line data={humidityData} />
            </ChartContainer>
          </Card>
        </CenterColumn>

        {/* 📌 Columna Derecha */}
        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <p>🛠️ Modo Manual | 🔄 Modo Automático</p>
            <p>🔥 Placa Térmica: <input type="range" min="0" max="100" /></p>
            <p>💡 Lámparas: <button>ON</button> <button>OFF</button></p>
            <p>🌫️ Humidificador: <ControlButton>ACTIVAR</ControlButton></p>
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p>🌞 <strong>Ciclo del Día</strong></p>
            <p>✅ Temperatura: Todo en orden</p>
            <p>⚠️ Iluminación: Necesita ajuste</p>
            <p>❌ Humedad: Necesita atención</p>
          </StatusPanel>
        </RightColumn>
      </Container>

      <Footer />
    </>
  );
};

export default Dashboard;
