import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";

const MIN_TEMP = 20;
const MAX_TEMP = 100;
const STEP = 5;


const SliderContainer = styled.div`
  width: 100%;
  height: 12px;
  background: #ccc;
  border-radius: 10px;
  position: relative;
  display: flex;
  align-items: center;
  cursor: pointer;
`;

const ProgressBar = styled.div`
  height: 100%;
  background: #4caf50;
  border-radius: 10px;
  width: ${({ value }) => ((value - MIN_TEMP) / (MAX_TEMP - MIN_TEMP)) * 100}%;
  transition: width 0.2s ease-out;
`;

const SliderCircle = styled.div`
  width: 22px;
  height: 22px;
  background: white;
  border: 3px solid #4caf50;
  border-radius: 50%;
  position: absolute;
  left: ${({ value }) =>
    `calc(${((value - MIN_TEMP) / (MAX_TEMP - MIN_TEMP)) * 100}% - 11px)`};
  top: 50%;
  transform: translateY(-50%);
  transition: left 0.2s ease-out;
  cursor: grab;
`;

const CustomSlider = ({ value, onChange }) => {
  const [dragging, setDragging] = useState(false);
  const sliderRef = useRef(null);

  useEffect(() => {
    if (dragging) {
      document.addEventListener("mousemove", handleMouseMove);
      document.addEventListener("mouseup", handleMouseUp);
    } else {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    }

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [dragging]);

  const handleMouseDown = (e) => {
    setDragging(true);
    handleMouseMove(e);
  };

  const handleMouseMove = (e) => {
    if (!sliderRef.current) return;

    const rect = sliderRef.current.getBoundingClientRect();
    const percent = ((e.clientX - rect.left) / rect.width) * 100;
    let newValue =
      MIN_TEMP + Math.round(((percent / 100) * (MAX_TEMP - MIN_TEMP)) / STEP) * STEP;

    newValue = Math.max(MIN_TEMP, Math.min(MAX_TEMP, newValue));
    onChange(newValue);
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  return (
    <SliderContainer ref={sliderRef} onMouseDown={handleMouseDown}>
      <ProgressBar value={value} />
      <SliderCircle value={value} />
    </SliderContainer>
  );
};

export default CustomSlider;
