import { useState, useEffect } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

// Función para enviar el email con la alerta
const enviarEmail = async (descripcion, data, setAlertStatus) => {
  setAlertStatus('Enviando alerta...');
  try {
    const response = await fetch('http://localhost:5000/send-email', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ descripcion, data }),
    });

    const result = await response.json();
    if (response.ok) {
      console.log('Alerta enviada con éxito:', result.message);
      setAlertStatus('Alerta enviada con éxito');
    } else {
      console.error('Error en la respuesta del servidor:', result.message);
      setAlertStatus(`Error: ${result.message}`);
    }
  } catch (error) {
    console.error('Error al enviar la alerta:', error);
    setAlertStatus(`Error de conexión: ${error.message}`);
  }
};

// Función para gestionar las alertas
const gestionarNotificacion = async (temperatura, ID_usuario, setAlertStatus, setLastAlertValue, setCooldown) => {
  const temperaturaFormateada = temperatura.toFixed(2);
  console.log('Temperatura formateada:', temperaturaFormateada);

  // Enviar alerta por email
  await enviarEmail('Temperatura fuera de rango', { temperatura: parseFloat(temperaturaFormateada) }, setAlertStatus);

  try {
    // Insertar notificación en la base de datos
    const notificacionResponse = await axios.post(
      'http://localhost:5000/api/addNotification',
      {
        tipo: 'temperatura',
        descripcion: `Temperatura fuera de rango: ${temperaturaFormateada}°C`,
        time_alert: new Date().toISOString().slice(0, 19).replace('T', ' '),
      },
      { headers: { 'Content-Type': 'application/json' } }
    );

    const idNotificacion = notificacionResponse.data.id_notificacion;
    if (idNotificacion) {
      // Insertar la relación usuario-notificación
      await axios.post(
        'http://localhost:5000/api/addNotificationUser',
        { id_notificacion: idNotificacion, id_user: ID_usuario },
        { headers: { 'Content-Type': 'application/json' } }
      );

      console.log('Relación notificación-usuario guardada con éxito');
    }

    // Controlar el cooldown de las alertas
    setLastAlertValue(temperatura);
    setCooldown(true);
    setTimeout(() => setCooldown(false), 10000); // Cooldown de 10 segundos
  } catch (error) {
    console.error('Error al gestionar la notificación:', error);
    setAlertStatus(`Error de conexión: ${error.message}`);
  }
};

const TemperaturaAlert = ({ temperatura, setTemperatura }) => {
  const [inputValue, setInputValue] = useState(temperatura.toString());
  const [lastAlertValue, setLastAlertValue] = useState(null);
  const [alertStatus, setAlertStatus] = useState('');
  const [cooldown, setCooldown] = useState(false);
  
  const token = localStorage.getItem('token');
  const ID_usuario = token ? jwtDecode(token).id : '';

  const handleInputChange = (e) => setInputValue(e.target.value);

  const handleUpdateTemperature = () => {
    const newTemperatura = parseFloat(inputValue);
    if (!isNaN(newTemperatura)) {
      setTemperatura(newTemperatura);
      gestionarNotificacion(newTemperatura, ID_usuario, setAlertStatus, setLastAlertValue, setCooldown);
    } else {
      setAlertStatus('Por favor ingrese un número válido');
      setTimeout(() => setAlertStatus(''), 3000);
    }
  };

  useEffect(() => {
    if (isNaN(temperatura)) return;

    // Verificar si la temperatura está fuera del rango
    const fueraDeRango = temperatura < 22 || temperatura > 32;

    if (fueraDeRango) {
      // Verificamos si debemos enviar una nueva alerta
      const debeEnviarAlerta =
        // No hay una alerta previa
        lastAlertValue === null || 
        // La temperatura ha cambiado significativamente (más de 1 grado)
        Math.abs(temperatura - lastAlertValue) >= 1 || 
        // La temperatura cruzó el umbral (de caliente a frío o viceversa)
        (lastAlertValue > 32 && temperatura < 22) || 
        (lastAlertValue < 22 && temperatura > 32);

      // Solo enviamos si debemos y no estamos en cooldown
      if (debeEnviarAlerta && !cooldown) {
        console.log('Enviando alerta por temperatura:', temperatura);
        gestionarNotificacion(temperatura, ID_usuario, setAlertStatus, setLastAlertValue, setCooldown);
      }
    } else {
      // Si volvió al rango normal, reiniciamos para que la próxima vez envíe alerta
      if (lastAlertValue !== null) {
        setLastAlertValue(null);
        setAlertStatus('Temperatura normalizada');
        setTimeout(() => setAlertStatus(''), 3000); // Limpiar el estado de alerta después de 3 segundos
      }
    }
  }, [temperatura, lastAlertValue, cooldown, ID_usuario]);

  useEffect(() => setInputValue(temperatura.toString()), [temperatura]);

  const getTemperatureStyle = () => {
    if (temperatura > 32) return { color: 'red', fontWeight: 'bold' };
    if (temperatura < 22) return { color: 'blue', fontWeight: 'bold' };
    return { color: 'green' };
  };

  return (
    <div className="PruebasNotificaciones">
      <h2>Monitoreo De Temperatura</h2>
      <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '15px' }}>
        <label htmlFor="temperatura">Temperatura (°C): </label>
        <input
          id="temperatura"
          type="number"
          value={inputValue}
          onChange={handleInputChange}
          step="0.1"
          style={{ width: '80px' }}
        />
        <button onClick={handleUpdateTemperature} style={{ padding: '5px 15px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
          Actualizar
        </button>
      </div>
      <p>Temperatura actual: <span style={getTemperatureStyle()}>{temperatura.toFixed(2)}°C</span></p>
      {temperatura < 22 && <p style={{ color: 'blue' }}>⚠️ Temperatura por debajo del rango normal (22°C - 32°C)</p>}
      {temperatura > 32 && <p style={{ color: 'red' }}>⚠️ Temperatura por encima del rango normal (22°C - 32°C)</p>}
      {cooldown && <p style={{ color: 'gray' }}>⏱️ Esperando para enviar la siguiente alerta...</p>}
      {alertStatus && <p className="alert-status">{alertStatus}</p>}
      {lastAlertValue !== null && (
        <div style={{ marginTop: '10px', fontSize: '0.9em', color: '#555' }}>
          Última alerta enviada: {lastAlertValue.toFixed(2)}°C
        </div>
      )}
    </div>
  );
};

const PruebasNotificaciones = () => {
  const [temperatura, setTemperatura] = useState(25); // Valor inicial dentro del rango normal
  return (
    <div>
      <TemperaturaAlert temperatura={temperatura} setTemperatura={setTemperatura} />
    </div>
  );
};

export default PruebasNotificaciones;
