import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import advertencia from "../assets/alerta-amarillo.png";
import ok from "../assets/comprobado.png";
import peligro from "../assets/sirena.png";
import dia from "../assets/manana.png";
import noche from "../assets/noche.png";

const Container = styled.div`
  padding: 50px 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
  align-items: stretch;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  margin-bottom: 15px;
`;

const ModeButton = styled.button`
  background: ${({ active }) => (active ? "#093609" : "#E0E2E6")};
  color: ${({ active }) => (active ? "white" : "black")};
  font-weight: bold;
  padding: 8px 16px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    background: #093609;
    color: white;
  }
`;

const ControlPanel = styled.div`
  background: rgba(123, 95, 61, 0.8);
  border-radius: 15px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  color: black;
  text-align: center;
  width: 100%;
`;

const SliderContainer = styled.div`
  width: 100%;
  margin: 15px 0;
`;

const LightControls = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
`;

const BigButton = styled.button`
  width: 100%;
  padding: 15px;
  font-size: 18px;
  font-weight: bold;
  color: white;
  background: ${({ active }) => (active ? "#4caf50" : "#E74C3C")};
  border: none;
  border-radius: 10px;
  cursor: pointer;
  margin-top: 10px;
`;

const DisabledOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  display: ${({ disabled }) => (disabled ? "block" : "none")};
`;

const Switch = ({ label, checked, onChange, disabled }) => (
  <label style={{ display: "flex", alignItems: "center", gap: "10px" }}>
    <span>{label}</span>
    <input type="checkbox" checked={checked} onChange={onChange} disabled={disabled} />
  </label>
);

const CustomSlider = ({ value, onChange, disabled }) => {
  const [dragging, setDragging] = useState(false);

  const handleMouseMove = useCallback(
    (e) => {
      if (!dragging) return;
      const rect = e.target.getBoundingClientRect();
      const percent = ((e.clientX - rect.left) / rect.width) * 100;
      let newValue = Math.max(20, Math.min(100, Math.round((percent / 100) * 80 + 20)));
      onChange(newValue);
    },
    [dragging, onChange]
  );

  return (
    <SliderContainer
      onMouseDown={() => setDragging(true)}
      onMouseMove={handleMouseMove}
      onMouseUp={() => setDragging(false)}
      onMouseLeave={() => setDragging(false)}
      style={{
        height: "10px",
        background: "#ccc",
        borderRadius: "5px",
        position: "relative",
        cursor: disabled ? "not-allowed" : "pointer",
      }}
    >
      <div
        style={{
          height: "100%",
          background: "#4caf50",
          borderRadius: "5px",
          width: `${((value - 20) / 80) * 100}%`,
        }}
      />
      <div
        style={{
          width: "20px",
          height: "20px",
          background: "white",
          borderRadius: "50%",
          border: "3px solid #4caf50",
          position: "absolute",
          left: `calc(${((value - 20) / 80) * 100}% - 10px)`,
          top: "-5px",
        }}
      />
      <span style={{ position: "absolute", top: "15px", left: "50%", transform: "translateX(-50%)" }}>
        {Math.round(value)}°C
      </span>
    </SliderContainer>
  );
};

const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(85);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(false);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaDePiel, setMudaDePiel] = useState(false);
  const [fase, setFase] = useState("Día Activo");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) navigate("/login");

    const hora = new Date().getHours();
    setFase(hora >= 6 && hora < 18 ? "Día Activo" : "Reposo Nocturno");
  }, [navigate]);

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        <ControlPanel>
          <h3>Panel de Control</h3>
          <ButtonGroup>
            <ModeButton active={!modoAutomatico} onClick={() => setModoAutomatico(false)}>
              Modo Manual
            </ModeButton>
            <ModeButton active={modoAutomatico} onClick={() => setModoAutomatico(true)}>
              Modo Automático
            </ModeButton>
          </ButtonGroup>

          <CustomSlider value={placaTermica} onChange={setPlacaTermica} disabled={modoAutomatico} />

          <LightControls>
            <Switch label="💡 Luz Visible" checked={luzVisible} onChange={() => setLuzVisible(!luzVisible)} disabled={modoAutomatico} />
            <Switch label="☀️ Luz UV" checked={luzUV} onChange={() => setLuzUV(!luzUV)} disabled={modoAutomatico} />
          </LightControls>

          <BigButton active={humidificador} onClick={() => setHumidificador(!humidificador)} disabled={modoAutomatico}>
            {humidificador ? "Desactivar Humidificador" : "Activar Humidificador"}
          </BigButton>

          <BigButton active={mudaDePiel} onClick={() => setMudaDePiel(!mudaDePiel)} disabled={modoAutomatico}>
            {mudaDePiel ? "Finalizar Muda de Piel" : "Iniciar Muda de Piel"}
          </BigButton>

          <DisabledOverlay disabled={modoAutomatico} />
        </ControlPanel>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
