import React, { Component } from 'react';

class TemperaturaAlert extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: props.temperatura.toString(),
      lastAlertValue: null,
      alertStatus: '',
      cooldown: false
    };
  }

  // Método para obtener la temperatura actual
  getTemperature = () => {
    return this.props.temperatura;
  }
  
  enviar_temperatura_alert = async (temp) => {
    this.setState({ alertStatus: 'Enviando alerta...' });
    try {
      // Asegurar que la temperatura se envía como string con 2 decimales
      const temperaturaFormateada = temp.toFixed(2);
      
      console.log("Enviando temperatura formateada:", temperaturaFormateada);
      
      const response = await fetch("http://localhost:5000/send-email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ 
          temperatura: parseFloat(temperaturaFormateada) // Convertir a número de nuevo
        }),
      });
      
      const data = await response.json();
      
      if (response.ok) {
        console.log("Alerta enviada con éxito:", data.message);
        this.setState({ 
          alertStatus: 'Alerta enviada con éxito',
          lastAlertValue: temp,
          cooldown: true
        });
        
        // Establecemos un tiempo de espera para evitar el spam de alertas
        setTimeout(() => {
          this.setState({ cooldown: false });
        }, 10000); // 10 segundos de cooldown
        
      } else {
        console.error("Error en la respuesta del servidor:", data.message);
        this.setState({ alertStatus: `Error: ${data.message}` });
      }
    } catch (error) {
      console.error("Error al enviar la alerta:", error);
      this.setState({ alertStatus: `Error de conexión: ${error.message}` });
    }
  };
  
  // Este manejador solo actualiza el input, no el valor de temperatura real
  handleInputChange = (e) => {
    this.setState({ inputValue: e.target.value });
  };
  
  // Función para actualizar la temperatura cuando se presiona el botón
  handleUpdateTemperature = () => {
    const newTemperatura = parseFloat(this.state.inputValue);
    
    // Verificar que es un número válido
    if (!isNaN(newTemperatura)) {
      this.props.setTemperatura(newTemperatura);
    } else {
      this.setState({ alertStatus: 'Por favor ingrese un número válido' });
      setTimeout(() => this.setState({ alertStatus: '' }), 3000);
    }
  };
  
  // Determinar el estilo basado en el rango de temperatura
  getTemperatureStyle = () => {
    const temperatura = this.getTemperature();
    if (temperatura > 32) return { color: 'red', fontWeight: 'bold' };
    if (temperatura < 22) return { color: 'blue', fontWeight: 'bold' };
    return { color: 'green' };
  };

  // React lifecycle methods
  componentDidUpdate(prevProps) {
    const temperatura = this.getTemperature();
    const { lastAlertValue, cooldown } = this.state;

    // Si cambió la temperatura, actualizar el input
    if (prevProps.temperatura !== temperatura) {
      this.setState({ inputValue: temperatura.toString() });
      
      // Lógica para verificar y enviar alertas
      // Solo enviar alerta si la temperatura es válida (no NaN)
      if (isNaN(temperatura)) return;
      
      // Verificamos si está fuera del rango permitido
      const fueraDeRango = temperatura < 22 || temperatura > 32;
      
      if (fueraDeRango) {
        // Verificamos si debemos enviar una nueva alerta
        const debeEnviarAlerta = 
          // No hay una alerta previa
          lastAlertValue === null || 
          // La temperatura ha cambiado significativamente (más de 1 grado)
          Math.abs(temperatura - lastAlertValue) >= 1 || 
          // La temperatura cruzó el umbral (de caliente a frío o viceversa)
          (lastAlertValue > 32 && temperatura < 22) || 
          (lastAlertValue < 22 && temperatura > 32);
        
        // Solo enviamos si debemos y no estamos en cooldown
        if (debeEnviarAlerta && !cooldown) {
          console.log("Enviando alerta por temperatura:", temperatura);
          this.enviar_temperatura_alert(temperatura);
        }
      } else {
        // Si volvió al rango normal, reiniciamos para que la próxima vez envíe alerta
        if (lastAlertValue !== null) {
          this.setState({ 
            lastAlertValue: null,
            alertStatus: 'Temperatura normalizada'
          });
          
          // Después de 3 segundos, limpiamos el mensaje
          setTimeout(() => {
            if (this.getTemperature() >= 22 && this.getTemperature() <= 32) {
              this.setState({ alertStatus: '' });
            }
          }, 3000);
        }
      }
    }
  }
  
  render() {
    const { inputValue, alertStatus, cooldown, lastAlertValue } = this.state;
    const temperatura = this.getTemperature();
    
    return (
      <div className="PruebasNotificaciones">
        <h2>Monitoreo De Temperatura</h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '15px' }}>
          <label htmlFor="temperatura">Temperatura (°C): </label>
          <input 
            id="temperatura"
            type="number" 
            value={inputValue} 
            onChange={this.handleInputChange} 
            step="0.1"
            style={{ width: '80px' }}
          />
          <button 
            onClick={this.handleUpdateTemperature}
            style={{
              padding: '5px 15px',
              backgroundColor: '#4CAF50',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Actualizar
          </button>
        </div>
        
        <p>Temperatura actual: <span style={this.getTemperatureStyle()}>{temperatura.toFixed(2)}°C</span></p>
        
        {temperatura < 22 && 
          <p style={{color: 'blue'}}>
            ⚠️ Temperatura por debajo del rango normal (22°C - 32°C)
          </p>
        }
        {temperatura > 32 && 
          <p style={{color: 'red'}}>
            ⚠️ Temperatura por encima del rango normal (22°C - 32°C)
          </p>
        }
        
        {cooldown && 
          <p style={{color: 'gray'}}>
            ⏱️ Esperando para enviar la siguiente alerta...
          </p>
        }
        
        {alertStatus && <p className="alert-status">{alertStatus}</p>}
        
        {lastAlertValue !== null && (
          <div style={{marginTop: '10px', fontSize: '0.9em', color: '#555'}}>
            Última alerta enviada: {lastAlertValue.toFixed(2)}°C
          </div>
        )}
      </div>
    );
  }
}

export default TemperaturaAlert;
