import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";
import notificationIcon from "../assets/campana.png";
import userIcon from "../assets/usuario.png";

const NavBar = styled.nav`
  background-color: #B4864D;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 20px;
  position: relative;
`;

const IconButton = styled.img`
  width: 30px;
  cursor: pointer;
`;

const Menu = styled.div`
  position: absolute;
  top: 60px;
  right: 10px;
  background-color: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0,0,0,0.3);
  overflow: hidden;
  z-index: 100;
`;

const MenuItem = styled.div`
  padding: 10px 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  font-weight: bold;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const Icon = styled.img`
  width: 20px;
  height: 20px;
  margin-right: 8px;
`;

const Header = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const isAuthenticated = localStorage.getItem("token") !== null;

  const toggleMenu = () => setMenuOpen(!menuVisible);

  const [menuVisible, setMenuVisible] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <NavBar>
      <img src={logo} alt="Logo" style={{width: '50px', cursor: 'pointer'}} onClick={() => navigate('/')}/>

      <ul style={{ display: "flex", gap: "20px", listStyle: "none" }}>
        <li onClick={() => navigate("/")}>Inicio</li>
        <li onClick={() => navigate("/dashboard")}>Centro de monitoreo y control</li>
      </ul>

      <RightSection>
        {localStorage.getItem("token") ? (
          <>
            <IconButton src={notificationIcon} alt="Notificaciones" />
            <IconButton 
              src={userIcon} 
              alt="Usuario"
              onClick={(e) => {
                e.stopPropagation();
                setMenuVisible(!menuVisible);
              }}
            />

            {menuVisible && (
              <Menu>
                <MenuItem onClick={() => navigate("/editar-datos")}>
                  <Icon src={userIcon} alt="Editar" /> Editar datos
                </MenuItem>
                <MenuItem onClick={handleLogout}>
                  <Icon src={notificationIcon} alt="Cerrar" /> Cerrar sesi贸n
                </MenuItem>
              </Menu>
            )}
          </>
        ) : (
          <LoginButton onClick={() => navigate("/login")}>Iniciar sesi贸n</LoginButton>
        )}
        <IconButton src={userIcon} alt="Usuario" onClick={() => setMenuVisible(!menuVisible)} />

        {menuVisible && (
          <Menu>
            <MenuItem onClick={() => navigate("/editar-datos")}>
              <Icon src={userIcon} alt="Editar" /> Editar datos
            </MenuItem>
            <MenuItem onClick={handleLogout}>
              <Icon src={userIcon} alt="Cerrar sesi贸n" /> Cerrar sesi贸n
            </MenuItem>
          </Menu>
        )}
      </RightSection>
    </NavBar>
  );
};

export default Header;
