import React, { Component } from 'react';
import TemperatureAlert from './TemperatureAlert';
import TemperatureUtility from './TemperatureUtility';  // Importa la utilidad

class PruebasNotificaciones extends Component {
  constructor(props) {
    super(props);
    this.state = {
      temperatura: 25 // Valor inicial dentro del rango normal
    };
    
    // Si existe una referencia global del estado
    if (window.globalTemperature === undefined) {
      window.globalTemperature = this.state.temperatura;
    } else {
      this.state.temperatura = window.globalTemperature;
    }
  }
  
  // Método para acceder a la temperatura desde fuera
  getTemperature = () => {
    return this.state.temperatura;
  }
  
  // Método para actualizar la temperatura
  setTemperatura = (newTemp) => {
    this.setState({ temperatura: newTemp });
    // Actualizar también la variable global y notificar a todos los suscriptores
    window.globalTemperature = newTemp;
    TemperatureUtility.setTemperature(newTemp); // Notificamos a los suscriptores
  }
  
  render() {
    return (
      <div>
        <TemperatureAlert 
          temperatura={this.state.temperatura} 
          setTemperatura={this.setTemperatura} 
        />
      </div>
    );
  }
}

export default PruebasNotificaciones;
