import React, { useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar componentes de Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// üìå Estilos generales
const Container = styled.div`
  padding-top: 100px; /* üîπ M√°s separaci√≥n con el Header */
  padding: 40px;
  background: white;
  border-radius: 20px;
  width: 95%;
  max-width: 1400px;
  margin: auto;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  height: auto;
`;

// üìå Tarjetas principales
const Card = styled.div`
  background: #a77e4f;
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  width: 100%;
`;

const CardTitle = styled.h3`
  background: #216c2a;
  padding: 10px;
  border-radius: 10px;
  margin-bottom: 15px;
`;

// üìå Contenedores de columnas
const LeftColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const RightColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

// üìå Paneles de control y estado general
const ControlPanel = styled(Card)`
  background: #5d4037;
`;

const StatusPanel = styled(Card)`
  background: #3e2723;
`;

// üìå Elementos espec√≠ficos
const Info = styled.p`
  font-size: 22px;
  font-weight: bold;
  margin: 10px 0;
`;

const ControlButton = styled.button`
  background: #4CAF50;
  border: none;
  color: white;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
  border-radius: 5px;
`;

const Dashboard = () => {
  const navigate = useNavigate();

  // üìå **Verificar si el usuario est√° autenticado**
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirige al login si no hay token
    }
  }, [navigate]);

  // üìå Datos de las gr√°ficas
  const temperatureData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Temperatura", data: [27, 28, 29, 30, 29, 28], borderColor: "orange", backgroundColor: "rgba(255, 165, 0, 0.5)" }]
  };

  const illuminationData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Intensidad UV", data: [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], borderColor: "yellow", backgroundColor: "rgba(255, 255, 0, 0.5)" }]
  };

  const humidityData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Humedad %", data: [30, 35, 40, 38, 32, 30], borderColor: "blue", backgroundColor: "rgba(0, 0, 255, 0.5)" }]
  };

  return (
    <>
      <Header showUserIcon={true} /> {/* üîπ Mostrar icono de usuario y notificaciones en Header */}
      <Container>
        {/* üìå Columna Izquierda - Par√°metros */}
        <LeftColumn>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <Info>M√≠nima: 29¬∞C - M√°xima: 32¬∞C</Info>
          </Card>

          <Card>
            <CardTitle>Iluminaci√≥n</CardTitle>
            <Info>Intensidad UV: 0.3</Info>
          </Card>

          <Card>
            <CardTitle>Humedad</CardTitle>
            <Info>Humedad: 30%</Info>
          </Card>
        </LeftColumn>

        {/* üìå Columna Central - Gr√°ficas */}
        <CenterColumn>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <Line data={temperatureData} />
          </Card>

          <Card>
            <CardTitle>Iluminaci√≥n</CardTitle>
            <Line data={illuminationData} />
          </Card>

          <Card>
            <CardTitle>Humedad</CardTitle>
            <Line data={humidityData} />
          </Card>
        </CenterColumn>

        {/* üìå Columna Derecha - Panel de Control & Estado General */}
        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <p>Modo Manual | Modo Autom√°tico</p>
            <p>Placa T√©rmica: <input type="range" min="0" max="100" /></p>
            <p>L√°mparas: <button>ON</button> <button>OFF</button></p>
            <p>Humidificador: <ControlButton>ACTIVAR</ControlButton></p>
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p><strong>Ciclo del D√≠a:</strong></p>
            <p>‚úÖ Temperatura: Todo en orden</p>
            <p>‚ö†Ô∏è Iluminaci√≥n: Necesita ajuste</p>
            <p>‚ùå Humedad: Necesita atenci√≥n</p>
          </StatusPanel>
        </RightColumn>
      </Container>

      <Footer />
    </>
  );
};

export default Dashboard;
