import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Estilos Generales
const Container = styled.div`
  padding: 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

// 📌 Botones de Modo de Control
const ButtonGroup = styled.div`
  display: flex;
  gap: 35x;
  margin-bottom: 15px;
`;

const ModeButton = styled.button`
  background: ${({ active }) => (active ? "#093609" : "#E0E2E6")};
  color: ${({ active }) => (active ? "white" : "black")};
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  padding: 10px 15px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    background: #093609;
    color: white;
  }
`;

// 📌 Contenedor para los interruptores (Switches)
const SwitchContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 80%;
  margin-top: 10px;
`;

// 📌 Checkbox para los switches
const Checkbox = styled.input`
  transform: scale(1.5);
`;

// 📌 Tarjetas con títulos sobresalientes
const Card = styled.div`
  background:rgba(123, 95, 61, 0.76);
  padding: 15px;
  border-radius: 50px; /* Esquinas más redondeadas */
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5), /* Sombra interna */
              10px 10px 20px rgba(245, 239, 230, 0.2); /* Sombra externa */
  backdrop-filter: blur(10000px); /* Suaviza el fondo */
  filter: drop-shadow(5px 5px 10px rgba(248, 202, 132, 3)); /* Efecto de profundidad */
  border: 1px solid rgba(248, 216, 186, 0.25); /* Borde suave */
  text-align: center;
  opacity: 1.2; /* Transparencia ligera */
  position: relative; /* Permite que el botón se posicione dentro */
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  height: 100%;
`;

const CardTitle = styled.h3`
  background: #093609;
  padding: 12px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  text-align: center;
`;

// 📌 Columnas y Alturas Uniformes
const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled(Column)`
  justify-content: space-between;
`;

const RightColumn = styled(Column)`
  justify-content: space-between;
  height: 100%;
`;

// 📌 Panel de Control con Diseño Exacto
const ControlPanel = styled(Card)`
  
  padding: 15px;
  border-radius: 50px; /* Esquinas más redondeadas */
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5), /* Sombra interna */
              10px 10px 20px rgba(245, 239, 230, 0.2); /* Sombra externa */
  backdrop-filter: blur(10000px); /* Suaviza el fondo */
  filter: drop-shadow(5px 5px 10px rgba(248, 202, 132, 3)); /* Efecto de profundidad */
  border: 1px solid rgba(248, 216, 186, 0.25); /* Borde suave */
  text-align: center;
  opacity: 1.2; /* Transparencia ligera */
  position: relative; /* Permite que el botón se posicione dentro */
  padding-top: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const DisabledOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  display: ${({ disabled }) => (disabled ? "block" : "none")};
`;

// 📌 Slider de Placa Térmica
const Slider = styled.input`
  width: 80%;
  margin: 10px 0;
`;

// 📌 Interruptores y Botón
const Switch = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 10px 0;
`;

const ControlButton = styled.button`
  background: #4caf50;
  border: none;
  color: white;
  padding: 10px;
  font-size: 14px;
  cursor: pointer;
  border-radius: 8px;
`;

// 📌 Contenedor de Gráficas (Mantener la misma altura)
const ChartContainer = styled.div`
  background: white;
  padding: 15px;
  border-radius: 15px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  height: 250px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

// 📌 Estado General
const StatusPanel = styled(Card)`
  background: rgb(239,191,134);
    background: -moz-linear-gradient(90deg, rgba(239,191,134,0.5102415966386555) 0%, rgba(236,137,19,0.4990371148459384) 51%, rgba(239,191,134,0.5046393557422969) 100%);
    background: -webkit-linear-gradient(90deg, rgba(239,191,134,0.5102415966386555) 0%, rgba(236,137,19,0.4990371148459384) 51%, rgba(239,191,134,0.5046393557422969) 100%);
    background: linear-gradient(90deg, rgba(239,191,134,0.5102415966386555) 0%, rgba(236,137,19,0.4990371148459384) 51%, rgba(239,191,134,0.5046393557422969) 100%);
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#efbf86",endColorstr="#efbf86",GradientType=1);
  padding: 15px;
  border-radius: 50px; /* Esquinas más redondeadas */
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5), /* Sombra interna */
              10px 10px 20px rgba(245, 239, 230, 0.2); /* Sombra externa */
  backdrop-filter: blur(10000px); /* Suaviza el fondo */
  filter: drop-shadow(5px 5px 10px rgba(248, 202, 132, 3)); /* Efecto de profundidad */
  border: 1px solid rgba(248, 216, 186, 0.25); /* Borde suave */
  text-align: center;
  opacity: 1.2; /* Transparencia ligera */
  position: relative; /* Permite que el botón se posicione dentro */
  height: 100%;
`;

const StatusItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 18px;
  font-weight: bold;
  background: ${({ color }) => color || "gray"};
  padding: 10px;
  border-radius: 10px;
  margin-top: 10px;
`;

const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(85);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(true);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaPiel, setMudaPiel] = useState(false);
  const [cicloDia, setCicloDia] = useState("mañana");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }

    const hora = new Date().getHours();
    if (hora >= 6 && hora < 12) setCicloDia("mañana");
    else if (hora >= 12 && hora < 18) setCicloDia("tarde");
    else setCicloDia("noche");
  }, [navigate]);

  // 📌 Datos de gráficas
  const chartData = (label, data, color) => ({
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label, data, borderColor: color, backgroundColor: `${color}50` }],
  });

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        {/* 📌 Columna Izquierda */}
        <Column>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <p>🌡️ 29°C - 32°C</p>
          </Card>
          <Card>
            <CardTitle>Iluminación</CardTitle>
            <p>☀️ Intensidad UV: 0.3</p>
          </Card>
          <Card>
            <CardTitle>Humedad</CardTitle>
            <p>💧 30%</p>
          </Card>
        </Column>

        {/* 📌 Columna Central */}
        <CenterColumn>
          <ChartContainer>
            <Line data={chartData("Temperatura", [27, 28, 29, 30, 29, 28], "#FF9800")} />
          </ChartContainer>
          <ChartContainer>
            <Line data={chartData("Intensidad UV", [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], "#FFC107")} />
          </ChartContainer>
          <ChartContainer>
            <Line data={chartData("Humedad %", [30, 35, 40, 38, 32, 30], "#2196F3")} />
          </ChartContainer>
        </CenterColumn>

        {/* 📌 Columna Derecha */}
        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <ButtonGroup>
              <ModeButton active={!modoAutomatico} onClick={() => setModoAutomatico(false)}>
                Modo Manual
              </ModeButton>
              <ModeButton active={modoAutomatico} onClick={() => setModoAutomatico(true)}>
                Modo Automático
              </ModeButton>
            </ButtonGroup>

            <Slider
              type="range"
              min="0"
              max="100"
              value={placaTermica}
              onChange={(e) => setPlacaTermica(e.target.value)}
              disabled={modoAutomatico}
            />

            <SwitchContainer>
              <Switch>
                <span>💡 Visible</span>
                <Checkbox type="checkbox" checked={luzVisible} onChange={() => setLuzVisible(!luzVisible)} disabled={modoAutomatico} />
              </Switch>
              <Switch>
                <span>UV</span>
                <Checkbox type="checkbox" checked={luzUV} onChange={() => setLuzUV(!luzUV)} disabled={modoAutomatico} />
              </Switch>
            </SwitchContainer>

            <ControlButton onClick={() => setHumidificador(!humidificador)} disabled={modoAutomatico}>
              ACTIVAR
            </ControlButton>

            <Switch>
              <span>Muda de Piel</span>
              <Checkbox type="checkbox" checked={mudaPiel} onChange={() => setMudaPiel(!mudaPiel)} disabled={modoAutomatico} />
            </Switch>

            <DisabledOverlay disabled={modoAutomatico} />
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p>🌞 Ciclo del Día: {cicloDia}</p>
            <StatusItem color="green">✅ Temperatura: Todo en orden</StatusItem>
            <StatusItem color="yellow">⚠️ Iluminación: Necesita ajuste</StatusItem>
            <StatusItem color="red">❌ Humedad: Necesita atención</StatusItem>
          </StatusPanel>
        </RightColumn>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
