import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// üìå Estilos Generales
const Container = styled.div`
  padding: 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

// üìå Botones de Modo de Control
const ButtonGroup = styled.div`
  display: flex;
  gap: 50x;
  margin-bottom: 90px;
`;

const ModeButton = styled.button`
  background: ${({ active }) => (active ? "#093609" : "#E0E2E6")};
  color: ${({ active }) => (active ? "white" : "black")};
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  padding: 6px 18px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    background: #093609;
    color: white;
  }
`;

// üìå Contenedor para los interruptores (Switches)
const SwitchContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 80%;
  margin-top: 10px;
`;

// üìå Checkbox para los switches
const Checkbox = styled.input`
  transform: scale(1.5);
`;

// üìå Tarjetas con t√≠tulos sobresalientes
const Card = styled.div`
  background:rgba(123, 95, 61, 0.76);
  padding: 15px;
  border-radius: 50px; /* Esquinas m√°s redondeadas */
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5), /* Sombra interna */
              10px 10px 20px rgba(245, 239, 230, 0.2); /* Sombra externa */
  backdrop-filter: blur(10000px); /* Suaviza el fondo */
  filter: drop-shadow(5px 5px 10px rgba(248, 202, 132, 3)); /* Efecto de profundidad */
  border: 1px solid rgba(248, 216, 186, 0.25); /* Borde suave */
  text-align: center;
  opacity: 1.2; /* Transparencia ligera */
  position: relative; /* Permite que el bot√≥n se posicione dentro */
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  height: 100%;
`;

const CardTitle = styled.h3`
  background: #093609;
  padding: 12px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  text-align: center;
`;

// üìå Columnas y Alturas Uniformes
const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled(Column)`
  justify-content: space-between;
`;

const RightColumn = styled(Column)`
  justify-content: space-between;
  height: 100%;
`;

// üìå Panel de Control con Dise√±o Exacto
const ControlPanel = styled(Card)`
  background:rgba(123, 95, 61, 0.76);
  padding: 15px;
  border-radius: 50px; /* Esquinas m√°s redondeadas */
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5), /* Sombra interna */
              10px 10px 20px rgba(245, 239, 230, 0.2); /* Sombra externa */
  backdrop-filter: blur(10000px); /* Suaviza el fondo */
  filter: drop-shadow(5px 5px 10px rgba(248, 202, 132, 3)); /* Efecto de profundidad */
  border: 1px solid rgba(248, 216, 186, 0.25); /* Borde suave */
  text-align: center;
  opacity: 1.2; /* Transparencia ligera */
  position: relative; /* Permite que el bot√≥n se posicione dentro */
  padding-top: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const DisabledOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  display: ${({ disabled }) => (disabled ? "block" : "none")};
`;


// üìå Interruptores y Bot√≥n
const Switch = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 10px 0;
`;

const ControlButton = styled.button`
  background: #4caf50;
  border: none;
  color: white;
  padding: 10px;
  font-size: 14px;
  cursor: pointer;
  border-radius: 8px;
`;

// üìå Contenedor de Gr√°ficas (Mantener la misma altura)
const ChartContainer = styled.div`
  background: white;
  padding: 15px;
  border-radius: 15px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  height: 250px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

// üìå Estado General
const StatusPanel = styled(Card)`
  background:rgba(123, 95, 61, 0.76);
  padding: 15px;
  border-radius: 50px; /* Esquinas m√°s redondeadas */
  box-shadow: inset -5px -5px 10px rgba(238, 209, 146, 0.5), /* Sombra interna */
              10px 10px 20px rgba(245, 239, 230, 0.2); /* Sombra externa */
  backdrop-filter: blur(10000px); /* Suaviza el fondo */
  filter: drop-shadow(5px 5px 10px rgba(248, 202, 132, 3)); /* Efecto de profundidad */
  border: 1px solid rgba(248, 216, 186, 0.25); /* Borde suave */
  text-align: center;
  opacity: 1.2; /* Transparencia ligera */
  position: relative; /* Permite que el bot√≥n se posicione dentro */
  height: 100%;
`;

const StatusItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 18px;
  font-weight: bold;
  background: ${({ color }) => color || "gray"};
  padding: 10px;
  border-radius: 10px;
  margin-top: 10px;
`;


// üìå Rango de temperatura
const MIN_TEMP = 20; // Temperatura m√≠nima en ¬∞C
const MAX_TEMP = 100; // Temperatura m√°xima en ¬∞C
const STEP = 5; // Saltos de temperatura en ¬∞C

// üìå Contenedor del Slider
const SliderContainer = styled.div`
  width: 90%;
  height: 12px;
  background: #ccced0;
  border-radius: 1000px;
  position: relative;
  display: flex;
  align-items: center;
  cursor: pointer;
  margin-top: 20px;
`;

// üìå Barra de Progreso del Slider
const ProgressBar = styled.div`
  height: 100%;
  background: #4caf50;
  border-radius: 1000px;
  width: ${({ value }) => ((value - MIN_TEMP) / (MAX_TEMP - MIN_TEMP)) * 100}%;
  transition: width 0.3s ease-out;
`;

// üìå C√≠rculo Deslizante
const SliderCircle = styled.div`
  width: 22px;
  height: 22px;
  background: white;
  border: 3px solid #4caf50;
  border-radius: 50%;
  position: absolute;
  left: ${({ value }) =>
    `calc(${((value - MIN_TEMP) / (MAX_TEMP - MIN_TEMP)) * 100}% - 11px)`};
  top: 50%;
  transform: translateY(-50%);
  transition: left 0.2s ease-out;
  cursor: grab;
`;

// üìå Rayitas en el Slider
const MarkersContainer = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  pointer-events: none;
`;

const Marker = styled.div`
  width: 2px;
  height: ${({ isMajor }) => (isMajor ? "15px" : "10px")};
  background: ${({ active }) => (active ? "#4caf50" : "white")};
  opacity: 0.5;
  position: relative;

  &:hover::after {
    content: "${({ temp }) => temp}¬∞C";
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    color: white;
    font-size: 12px;
    background: rgba(0, 0, 0, 0.7);
    padding: 3px 6px;
    border-radius: 4px;
    white-space: nowrap;
  }
`;

const CustomSlider = ({ value, onChange }) => {
  const [dragging, setDragging] = useState(false);

  const handleMouseDown = () => {
    setDragging(true);
  };

  const handleMouseMove = (e) => {
    if (!dragging) return;

    const rect = e.target.parentElement.getBoundingClientRect();
    const percent = ((e.clientX - rect.left) / rect.width) * 100;
    let newValue =
      MIN_TEMP + Math.round(((percent / 100) * (MAX_TEMP - MIN_TEMP)) / STEP) * STEP;

    // Asegurar que el valor est√© dentro del rango
    newValue = Math.max(MIN_TEMP, Math.min(MAX_TEMP, newValue));

    onChange(newValue);
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  return (
    <SliderContainer
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseUp} // Detiene el drag si el mouse sale del √°rea
    >
      <ProgressBar value={value} />
      <MarkersContainer>
        {Array.from({ length: (MAX_TEMP - MIN_TEMP) / STEP + 1 }, (_, i) => MIN_TEMP + i * STEP).map(
          (temp) => (
            <Marker key={temp} temp={temp} active={temp <= value} isMajor={temp % 10 === 0} />
          )
        )}
      </MarkersContainer>
      <SliderCircle value={value} />
    </SliderContainer>
  );
};

const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(85);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(true);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaPiel, setMudaPiel] = useState(false);
  const [cicloDia, setCicloDia] = useState("ma√±ana");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }

    const hora = new Date().getHours();
    if (hora >= 6 && hora < 12) setCicloDia("ma√±ana");
    else if (hora >= 12 && hora < 18) setCicloDia("tarde");
    else setCicloDia("noche");
  }, [navigate]);

  // üìå Datos de gr√°ficas
  const chartData = (label, data, color) => ({
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label, data, borderColor: color, backgroundColor: `${color}50` }],
  });

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        {/* üìå Columna Izquierda */}
        <Column>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <p>üå°Ô∏è 29¬∞C - 32¬∞C</p>
          </Card>
          <Card>
            <CardTitle>Iluminaci√≥n</CardTitle>
            <p>‚òÄÔ∏è Intensidad UV: 0.3</p>
          </Card>
          <Card>
            <CardTitle>Humedad</CardTitle>
            <p>üíß 30%</p>
          </Card>
        </Column>

        {/* üìå Columna Central */}
        <CenterColumn>
          <ChartContainer>
            <Line data={chartData("Temperatura", [27, 28, 29, 30, 29, 28], "#FF9800")} />
          </ChartContainer>
          <ChartContainer>
            <Line data={chartData("Intensidad UV", [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], "#FFC107")} />
          </ChartContainer>
          <ChartContainer>
            <Line data={chartData("Humedad %", [30, 35, 40, 38, 32, 30], "#2196F3")} />
          </ChartContainer>
        </CenterColumn>

        {/* üìå Columna Derecha */}
        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <ButtonGroup>
              <ModeButton active={!modoAutomatico} onClick={() => setModoAutomatico(false)}>
                Modo Manual
              </ModeButton>
              <ModeButton active={modoAutomatico} onClick={() => setModoAutomatico(true)}>
                Modo Autom√°tico
              </ModeButton>
            </ButtonGroup>


            <SwitchContainer>
              <Switch>
                <span>üí° Visible</span>
                <Checkbox type="checkbox" checked={luzVisible} onChange={() => setLuzVisible(!luzVisible)} disabled={modoAutomatico} />
              </Switch>
              <Switch>
                <span>UV</span>
                <Checkbox type="checkbox" checked={luzUV} onChange={() => setLuzUV(!luzUV)} disabled={modoAutomatico} />
              </Switch>
            </SwitchContainer>

            <ControlButton onClick={() => setHumidificador(!humidificador)} disabled={modoAutomatico}>
              ACTIVAR
            </ControlButton>

            <Switch>
              <span>Muda de Piel</span>
              <Checkbox type="checkbox" checked={mudaPiel} onChange={() => setMudaPiel(!mudaPiel)} disabled={modoAutomatico} />
            </Switch>

            <DisabledOverlay disabled={modoAutomatico} />
            <h4>üî• Placa T√©rmica</h4>
            <CustomSlider value={placaTermica} onChange={setPlacaTermica} />
            <p>{Math.round(placaTermica)}%</p>
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p>üåû Ciclo del D√≠a: {cicloDia}</p>
            <StatusItem color="green">‚úÖ Temperatura: Todo en orden</StatusItem>
            <StatusItem color="yellow">‚ö†Ô∏è Iluminaci√≥n: Necesita ajuste</StatusItem>
            <StatusItem color="red">‚ùå Humedad: Necesita atenci√≥n</StatusItem>
          </StatusPanel>
        </RightColumn>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
