import React, { useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import InputField from "../components/InputField";
import Button from "../components/Button";
import gecko from "../assets/reptil-gecko.png";
import planta from "../assets/planta.png";

const Container = styled.div`
  padding-top: ${(props) => props.headerHeight + 40}px;
  padding-left: 40px;
  padding-right: 40px;
  padding-bottom: 75px;
  background: white;
  border-radius: 20px;
  width: 80%;
  max-width: 500px;
  margin: auto;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const AuthBox = styled.div`
  background: linear-gradient(90deg, rgba(239,191,134,0.51) 0%, rgba(236,137,19,0.5) 51%, rgba(239,191,134,0.5) 100%);
  padding: 30px;
  border-radius: 30px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  text-align: center;
  width: 90%;
`;

const Title = styled.h2`
  color: black;
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin: 10px 0 5px;
  font-weight: bold;
  color: black;
`;

const ErrorMessage = styled.p`
  color: red;
  font-size: 14px;
`;

const AuthForm = ({ isRegister }) => {
  const [headerHeight, setHeaderHeight] = useState(0);
  const [Usuario, setUsuario] = useState("");
  const [Correo, setCorreo] = useState("");
  const [Contrasena, setContrasena] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!Usuario || !Contrasena || (isRegister && !Correo)) {
      setError("Todos los campos son obligatorios");
      return;
    }

    if (isRegister && Contrasena !== confirmPassword) {
      setError("Las contraseñas no coinciden");
      return;
    }

    try {
      const endpoint = isRegister ? "register" : "login";
      const payload = isRegister ? { Usuario, Correo, Contrasena } : { Usuario, Contrasena };
      
      const response = await fetch(`http://localhost:5000/${endpoint}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (!response.ok) {
        setError(data.error);
        return;
      }

      if (!isRegister) {
        localStorage.setItem("token", data.token);
        navigate("/dashboard");
      } else {
        navigate("/login");
      }
    } catch (error) {
      setError("Error en la conexión con el servidor");
    }
  };

  return (
    <>
      <Header setHeaderHeight={setHeaderHeight} hideLoginButton={true} />
      <Container headerHeight={headerHeight}>
        <AuthBox>
          <Title>{isRegister ? "REGISTRO" : "INICIO DE SESIÓN"}</Title>
          {error && <ErrorMessage>{error}</ErrorMessage>}
          <form onSubmit={handleSubmit}>
            <Label>Usuario</Label>
            <InputField
              type="text"
              placeholder="Ingresa tu usuario"
              value={Usuario}
              onChange={(e) => setUsuario(e.target.value)}
            />

            {isRegister && (
              <>
                <Label>Correo Electrónico</Label>
                <InputField
                  type="email"
                  placeholder="Ingresa tu correo"
                  value={Correo}
                  onChange={(e) => setCorreo(e.target.value)}
                />
              </>
            )}

            <Label>Contraseña</Label>
            <InputField
              type="password"
              placeholder="Ingresa tu contraseña"
              value={Contrasena}
              onChange={(e) => setContrasena(e.target.value)}
            />

            {isRegister && (
              <>
                <Label>Confirmar Contraseña</Label>
                <InputField
                  type="password"
                  placeholder="Confirma tu contraseña"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
              </>
            )}

            <Button text={isRegister ? "REGISTRARSE" : "INICIAR SESIÓN"} type="submit" />
          </form>
        </AuthBox>
      </Container>
      <Footer />
    </>
  );
};

export default AuthForm;
