import React, { useEffect, useState, useRef } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import advertencia from "../assets/alerta-amarillo.png";
import ok from "../assets/comprobado.png";
import peligro from "../assets/sirena.png";
import manana from "../assets/manana.png";
import tarde from "../assets/atardecer.png";
import noche from "../assets/noche.png";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// **SLIDER COMPONENT**
const MIN_TEMP = 20;
const MAX_TEMP = 100;
const STEP = 5;

const SliderContainer = styled.div`
  width: 100%;
  height: 12px;
  background: #ccced0;
  border-radius: 10px;
  position: relative;
  display: flex;
  align-items: center;
  cursor: pointer;
  margin-top: 20px;
`;

const ProgressBar = styled.div`
  height: 100%;
  background: #4caf50;
  border-radius: 1000px;
  width: ${({ value }) => ((value - MIN_TEMP) / (MAX_TEMP - MIN_TEMP)) * 100}%;
  transition: width 0.2s ease-out;
`;

const SliderCircle = styled.div`
  width: 22px;
  height: 22px;
  background: white;
  border: 3px solid #4caf50;
  border-radius: 50%;
  position: absolute;
  left: ${({ value }) =>
    `calc(${((value - MIN_TEMP) / (MAX_TEMP - MIN_TEMP)) * 100}% - 11px)`};
  top: 50%;
  transform: translateY(-50%);
  transition: left 0.2s ease-out;
  cursor: grab;
`;

const MarkersContainer = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  pointer-events: none;
`;

const Marker = styled.div`
  width: 2px;
  height: ${({ isMajor }) => (isMajor ? "15px" : "10px")};
  background: ${({ active }) => (active ? "#4caf50" : "white")};
  opacity: 0.5;
`;

const CustomSlider = ({ value, onChange }) => {
  const [dragging, setDragging] = useState(false);
  const sliderRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!dragging || !sliderRef.current) return;
      const rect = sliderRef.current.getBoundingClientRect();
      const percent = ((e.clientX - rect.left) / rect.width) * 100;
      let newValue = MIN_TEMP + Math.round(((percent / 100) * (MAX_TEMP - MIN_TEMP)) / STEP) * STEP;
      newValue = Math.max(MIN_TEMP, Math.min(MAX_TEMP, newValue));
      onChange(newValue);
    };

    const handleMouseUp = () => setDragging(false);

    if (dragging) {
      document.addEventListener("mousemove", handleMouseMove);
      document.addEventListener("mouseup", handleMouseUp);
    }

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [dragging, onChange]);

  return (
    <SliderContainer ref={sliderRef} onMouseDown={() => setDragging(true)}>
      <ProgressBar value={value} />
      <MarkersContainer>
        {Array.from({ length: (MAX_TEMP - MIN_TEMP) / STEP + 1 }, (_, i) => MIN_TEMP + i * STEP).map(
          (temp) => (
            <Marker key={temp} temp={temp} active={temp <= value} isMajor={temp % 10 === 0} />
          )
        )}
      </MarkersContainer>
      <SliderCircle value={value} />
    </SliderContainer>
  );
};

// **DASHBOARD COMPONENT**
const Container = styled.div`
  padding: 50px 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
  align-items: stretch;
`;

const Card = styled.div`
  background: rgba(123, 95, 61, 0.8);
  border-radius: 15px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  color: black;
  text-align: center;
  height: 80%;
  margin-top: 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(85);
  const [cicloDia, setCicloDia] = useState("ma침ana");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) navigate("/login");

    const hora = new Date().getHours();
    if (hora >= 6 && hora < 12) setCicloDia("ma침ana");
    else if (hora >= 12 && hora < 18) setCicloDia("tarde");
    else setCicloDia("noche");
  }, [navigate]);

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        <Card>
          <h3>Panel de Control</h3>
          <button onClick={() => setModoAutomatico(!modoAutomatico)}>
            {modoAutomatico ? "Modo Autom치tico" : "Modo Manual"}
          </button>

          <h4>游댠 Placa T칠rmica</h4>
          <CustomSlider value={placaTermica} onChange={setPlacaTermica} />
          <p>{Math.round(placaTermica)}%</p>
        </Card>

        <Card>
          <h3>Estado General</h3>
          <p>Ciclo del D칤a: {cicloDia}</p>
          <img src={cicloDia === "ma침ana" ? manana : cicloDia === "tarde" ? tarde : noche} alt={cicloDia} />
        </Card>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
