import React, { useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Contenedor Principal (Grid Layout mejorado)
const Container = styled.div`
  padding: 20px 40px;
  margin: auto;
  max-width: 1400px;
  background: #f5f5f5; /* Color de fondo más claro */
  border-radius: 15px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 80vh; /* ✅ Se asegura que la altura sea correcta */
`;

// 📌 Tarjetas
const Card = styled.div`
  background: #8b5a2b;
  color: white;
  padding: 15px;
  border-radius: 10px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  width: 100%;
`;

const CardTitle = styled.h3`
  background: #216c2a;
  padding: 8px;
  border-radius: 8px;
  margin-bottom: 10px;
`;

// 📌 Columnas de la cuadrícula
const LeftColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const CenterColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const RightColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

// 📌 Panel de control y estado general
const ControlPanel = styled(Card)`
  background: #5d4037;
`;

const StatusPanel = styled(Card)`
  background: #3e2723;
`;

// 📌 Elementos
const Info = styled.p`
  font-size: 18px;
  font-weight: bold;
`;

const ControlButton = styled.button`
  background: #4CAF50;
  border: none;
  color: white;
  padding: 10px;
  font-size: 14px;
  cursor: pointer;
  border-radius: 5px;
`;

// 📌 Dashboard
const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }
  }, [navigate]);

  // 📌 Datos de las gráficas
  const temperatureData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Temperatura", data: [27, 28, 29, 30, 29, 28], borderColor: "orange", backgroundColor: "rgba(255, 165, 0, 0.5)" }]
  };

  const illuminationData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Intensidad UV", data: [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], borderColor: "yellow", backgroundColor: "rgba(255, 255, 0, 0.5)" }]
  };

  const humidityData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Humedad %", data: [30, 35, 40, 38, 32, 30], borderColor: "blue", backgroundColor: "rgba(0, 0, 255, 0.5)" }]
  };

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        {/* 📌 Columna Izquierda */}
        <LeftColumn>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <Info>🌡️ Mínima: 29°C - Máxima: 32°C</Info>
          </Card>

          <Card>
            <CardTitle>Iluminación</CardTitle>
            <Info>☀️ Intensidad UV: 0.3</Info>
          </Card>

          <Card>
            <CardTitle>Humedad</CardTitle>
            <Info>💧 Humedad: 30%</Info>
          </Card>
        </LeftColumn>

        {/* 📌 Columna Central */}
        <CenterColumn>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <Line data={temperatureData} />
          </Card>

          <Card>
            <CardTitle>Iluminación</CardTitle>
            <Line data={illuminationData} />
          </Card>

          <Card>
            <CardTitle>Humedad</CardTitle>
            <Line data={humidityData} />
          </Card>
        </CenterColumn>

        {/* 📌 Columna Derecha */}
        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <p>🛠️ Modo Manual | 🔄 Modo Automático</p>
            <p>🔥 Placa Térmica: <input type="range" min="0" max="100" /></p>
            <p>💡 Lámparas: <button>ON</button> <button>OFF</button></p>
            <p>🌫️ Humidificador: <ControlButton>ACTIVAR</ControlButton></p>
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p>🌞 <strong>Ciclo del Día</strong></p>
            <p>✅ Temperatura: Todo en orden</p>
            <p>⚠️ Iluminación: Necesita ajuste</p>
            <p>❌ Humedad: Necesita atención</p>
          </StatusPanel>
        </RightColumn>
      </Container>

      <Footer />
    </>
  );
};

export default Dashboard;
