import React from "react";
import styled from "styled-components";
import userIcon from "../assets/user-icon.png"; // ðŸ”¹ AsegÃºrate de que tienes la imagen
import bellIcon from "../assets/notification-icon.png";

const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #8b5a2b;
  padding: 15px 30px;
`;

const IconContainer = styled.div`
  display: flex;
  gap: 20px;
`;

const Icon = styled.img`
  width: 35px;
  height: 35px;
  cursor: pointer;
`;

const Header = ({ showUserIcon }) => {
  return (
    <HeaderContainer>
      <h2>GECKO MONITOREO</h2>
      {showUserIcon && (
        <IconContainer>
          <Icon src={bellIcon} alt="Notificaciones" />
          <Icon src={userIcon} alt="Usuario" />
        </IconContainer>
      )}
    </HeaderContainer>
  );
};

export default Header;
