import { useState } from 'react';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

// Función para enviar la alerta por correo
const enviarEmail = async (descripcion, valor, tipo, setAlertStatus) => {
  setAlertStatus(`Enviando alerta de ${tipo}...`);
  try {
    const response = await fetch('http://localhost:5000/send-email', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ descripcion, valor, tipo }),
    });

    const result = await response.json();
    if (response.ok) {
      console.log(`Alerta de ${tipo} enviada con éxito:`, result.message);
      setAlertStatus(`Alerta de ${tipo} enviada con éxito`);
    } else {
      console.error('Error en la respuesta del servidor:', result.message);
      setAlertStatus(`Error: ${result.message}`);
    }
  } catch (error) {
    console.error(`Error al enviar la alerta de ${tipo}:`, error);
    setAlertStatus(`Error de conexión: ${error.message}`);
  }
};

// Función para insertar la temperatura en la base de datos
const enviarTemperatura = async (ID_usuario, temperatura, Zona, Marca_tiempo) => {
  try {
    if (!ID_usuario || !temperatura || !Zona || !Marca_tiempo) {
      console.error('Datos incompletos:', { ID_usuario, temperatura, Zona, Marca_tiempo });
      throw new Error('Todos los campos son obligatorios');
    }

    console.log('Registrando temperatura en la base de datos:', {
      ID_usuario,
      Medicion: temperatura,
      Zona,
      Marca_tiempo
    });

    await axios.post(
      'http://localhost:5000/api/addtemperatura',
      {
        ID_usuario,
        Medicion: temperatura,
        Zona,
        Marca_tiempo
      },
      { headers: { "Content-Type": "application/json" }, timeout: 10000 }
    );

    console.log('Temperatura guardada correctamente.');
  } catch (error) {
    console.error('Error al guardar la temperatura:', error);
  }
};

// Función para insertar la humedad en la base de datos
const enviarHumedad = async (ID_usuario, humedad, Zona, Marca_tiempo) => {
  try {
    if (!ID_usuario || !humedad || !Zona || !Marca_tiempo) {
      console.error('Datos incompletos:', { ID_usuario, humedad, Zona, Marca_tiempo });
      throw new Error('Todos los campos son obligatorios');
    }

    console.log('Registrando humedad en la base de datos:', {
      ID_usuario,
      Medicion: humedad,
      Zona,
      Marca_tiempo
    });

    await axios.post(
      'http://localhost:5000/api/addhumedad',
      {
        ID_usuario,
        Medicion: humedad,
        Zona,
        Marca_tiempo
      },
      { headers: { "Content-Type": "application/json" }, timeout: 10000 }
    );

    console.log('Humedad guardada correctamente.');
  } catch (error) {
    console.error('Error al guardar la humedad:', error);
  }
};

// Función para gestionar alertas de temperatura
const gestionarNotificacionTemperatura = async (temperatura, ID_usuario, setAlertStatus, setLastAlertValue, lastAlertValue) => {
  const Marca_tiempo = new Date().toISOString().slice(0, 19).replace('T', ' ');

  // Insertar siempre la temperatura en la base de datos
  await enviarTemperatura(ID_usuario, temperatura, 'Zona 1', Marca_tiempo);

  const temperaturaFormateada = temperatura.toFixed(2);
  console.log('Temperatura formateada:', temperaturaFormateada);

  if (lastAlertValue !== null && Math.abs(temperatura - lastAlertValue) < 1) {
    console.log('Temperatura similar a la anterior, no se enviará alerta.');
    return;
  }

  if (temperatura < 22 || temperatura > 32) {
    try {
      await enviarEmail('Temperatura fuera de rango', temperatura, 'temperatura', setAlertStatus);

      const notificacionResponse = await axios.post(
        'http://localhost:5000/api/addNotification',
        {
          tipo: 'temperatura',
          descripcion: `Temperatura fuera de rango: ${temperaturaFormateada}°C`,
          time_alert: Marca_tiempo,
        },
        { headers: { 'Content-Type': 'application/json' } }
      );

      const idNotificacion = notificacionResponse.data.id_notificacion;
      if (idNotificacion) {
        await axios.post(
          'http://localhost:5000/api/addNotificationUser',
          { id_notificacion: idNotificacion, id_user: ID_usuario },
          { headers: { 'Content-Type': 'application/json' } }
        );
        console.log('Relación notificación-usuario guardada con éxito');
      }

      setLastAlertValue(temperatura);
    } catch (error) {
      console.error('Error al gestionar la notificación:', error);
      setAlertStatus(`Error de conexión: ${error.message}`);
    }
  }
};

// Función para gestionar alertas de humedad
const gestionarNotificacionHumedad = async (humedad, ID_usuario, setAlertStatus, setLastAlertValue, lastAlertValue) => {
  const Marca_tiempo = new Date().toISOString().slice(0, 19).replace('T', ' ');

  // Insertar siempre la humedad en la base de datos
  await enviarHumedad(ID_usuario, humedad, 'Zona 1', Marca_tiempo);

  const humedadFormateada = humedad.toFixed(2);
  console.log('Humedad formateada:', humedadFormateada);

  if (lastAlertValue !== null && Math.abs(humedad - lastAlertValue) < 1) {
    console.log('Humedad similar a la anterior, no se enviará alerta.');
    return;
  }

  if (humedad < 30 || humedad > 50) {
    try {
      await enviarEmail('Humedad fuera de rango', humedad, 'humedad', setAlertStatus);

      const notificacionResponse = await axios.post(
        'http://localhost:5000/api/addNotification',
        {
          tipo: 'humedad',
          descripcion: `Humedad fuera de rango: ${humedadFormateada}%`,
          time_alert: Marca_tiempo,
        },
        { headers: { 'Content-Type': 'application/json' } }
      );

      const idNotificacion = notificacionResponse.data.id_notificacion;
      if (idNotificacion) {
        await axios.post(
          'http://localhost:5000/api/addNotificationUser',
          { id_notificacion: idNotificacion, id_user: ID_usuario },
          { headers: { 'Content-Type': 'application/json' } }
        );
        console.log('Relación notificación-usuario guardada con éxito');
      }

      setLastAlertValue(humedad);
    } catch (error) {
      console.error('Error al gestionar la notificación:', error);
      setAlertStatus(`Error de conexión: ${error.message}`);
    }
  }
};

const TemperaturaAlert = ({ temperatura, setTemperatura }) => {
  const [inputValue, setInputValue] = useState(temperatura.toString());
  const [lastAlertValue, setLastAlertValue] = useState(null);
  const [alertStatus, setAlertStatus] = useState('');

  const token = localStorage.getItem('token');
  const ID_usuario = token ? jwtDecode(token).id : '';

  const handleInputChange = (e) => setInputValue(e.target.value);

  const handleUpdateTemperature = () => {
    const newTemperatura = parseFloat(inputValue);
    if (!isNaN(newTemperatura)) {
      setTemperatura(newTemperatura);
      gestionarNotificacionTemperatura(newTemperatura, ID_usuario, setAlertStatus, setLastAlertValue, lastAlertValue);
    } else {
      setAlertStatus('Por favor ingrese un número válido');
      setTimeout(() => setAlertStatus(''), 3000);
    }
  };

  return (
    <div className="MonitoreoTemperatura">
      <h2>Monitoreo De Temperatura</h2>
      <div>
        <label htmlFor="temperatura">Temperatura (°C): </label>
        <input id="temperatura" type="number" value={inputValue} onChange={handleInputChange} step="0.1" />
        <button onClick={handleUpdateTemperature}>Actualizar</button>
      </div>
      <p>Temperatura actual: <span>{temperatura.toFixed(2)}°C</span></p>
      {alertStatus && <p>{alertStatus}</p>}
    </div>
  );
};

const HumedadAlert = ({ humedad, setHumedad }) => {
  const [inputValue, setInputValue] = useState(humedad.toString());
  const [lastAlertValue, setLastAlertValue] = useState(null);
  const [alertStatus, setAlertStatus] = useState('');

  const token = localStorage.getItem('token');
  const ID_usuario = token ? jwtDecode(token).id : '';

  const handleInputChange = (e) => setInputValue(e.target.value);

  const handleUpdateHumedad = () => {
    const newHumedad = parseFloat(inputValue);
    if (!isNaN(newHumedad)) {
      setHumedad(newHumedad);
      gestionarNotificacionHumedad(newHumedad, ID_usuario, setAlertStatus, setLastAlertValue, lastAlertValue);
    } else {
      setAlertStatus('Por favor ingrese un número válido');
      setTimeout(() => setAlertStatus(''), 3000);
    }
  };

  return (
    <div className="MonitoreoHumedad">
      <h2>Monitoreo De Humedad</h2>
      <div>
        <label htmlFor="humedad">Humedad (%): </label>
        <input id="humedad" type="number" value={inputValue} onChange={handleInputChange} step="0.1" />
        <button onClick={handleUpdateHumedad}>Actualizar</button>
      </div>
      <p>Humedad actual: <span>{humedad.toFixed(2)}%</span></p>
      {alertStatus && <p>{alertStatus}</p>}
    </div>
  );
};

const PruebasNotificaciones = () => {
  const [temperatura, setTemperatura] = useState(25);
  const [humedad, setHumedad] = useState(40);
  
  return (
    <div className="PruebasNotificaciones">
      <TemperaturaAlert temperatura={temperatura} setTemperatura={setTemperatura} />
      <hr />
      <HumedadAlert humedad={humedad} setHumedad={setHumedad} />
    </div>
  );
};

export default PruebasNotificaciones;
