import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Estilos Generales
const Container = styled.div`
  padding: 50px 30px 30px; /* 🔹 Más espacio entre header y contenido */
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

const Card = styled.div`
  background: #a67c52;
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  height: 100%;
`;

const CardTitle = styled.h3`
  background: #3c722b;
  padding: 12px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  text-align: center;
`;

const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled(Column)`
  justify-content: space-between;
`;

const RightColumn = styled(Column)`
  justify-content: space-between;
  height: 100%;
`;

// 📌 Panel de Control con Modo Manual/Automático
const ControlPanel = styled(Card)`
  background: #5d4037;
  padding-top: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const ModeSwitch = styled.div`
  display: flex;
  justify-content: space-between;
  width: 80%;
  margin-bottom: 15px;
`;

const Slider = styled.input`
  width: 80%;
  margin: 10px 0;
`;

const Switch = styled.label`
  display: flex;
  align-items: center;
  gap: 10px;
  cursor: pointer;
`;

const Checkbox = styled.input`
  transform: scale(1.5);
`;

// 📌 Botón de Activación
const ControlButton = styled.button`
  background: #4caf50;
  border: none;
  color: white;
  padding: 10px;
  font-size: 14px;
  cursor: pointer;
  border-radius: 8px;
`;

// 📌 Estado General
const StatusPanel = styled(Card)`
  background: #3e2723;
  height: 100%;
`;

const StatusItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 18px;
  font-weight: bold;
  background: ${({ color }) => color || "gray"};
  padding: 10px;
  border-radius: 10px;
  margin-top: 10px;
`;

// 📌 Estado General con Imágenes
const StatusImage = styled.img`
  width: 25px;
  height: 25px;
`;

const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(50);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(false);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaPiel, setMudaPiel] = useState(false);

  const [cicloDia, setCicloDia] = useState("mañana");
  const [temperatura, setTemperatura] = useState(29);
  const [intensidadUV, setIntensidadUV] = useState(0.3);
  const [humedad, setHumedad] = useState(30);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }

    const hora = new Date().getHours();
    if (hora >= 6 && hora < 12) setCicloDia("mañana");
    else if (hora >= 12 && hora < 18) setCicloDia("tarde");
    else setCicloDia("noche");
  }, [navigate]);

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        <Column>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <p>🌡️ {temperatura}°C</p>
          </Card>
          <Card>
            <CardTitle>Iluminación</CardTitle>
            <p>☀️ Intensidad UV: {intensidadUV}</p>
          </Card>
          <Card>
            <CardTitle>Humedad</CardTitle>
            <p>💧 {humedad}%</p>
          </Card>
        </Column>

        <CenterColumn>
          <Card>
            <CardTitle>Panel de Control</CardTitle>
            <ModeSwitch>
              <button onClick={() => setModoAutomatico(false)}>Modo Manual</button>
              <button onClick={() => setModoAutomatico(true)}>Modo Automático</button>
            </ModeSwitch>
            <Slider type="range" min="0" max="100" value={placaTermica} disabled={modoAutomatico} />
            <Switch>
              <span>💡 Visible</span>
              <Checkbox type="checkbox" checked={luzVisible} disabled={modoAutomatico} onChange={() => setLuzVisible(!luzVisible)} />
              <span>UV</span>
              <Checkbox type="checkbox" checked={luzUV} disabled={modoAutomatico} onChange={() => setLuzUV(!luzUV)} />
            </Switch>
            <ControlButton disabled={modoAutomatico} onClick={() => setHumidificador(!humidificador)}>ACTIVAR</ControlButton>
            <Checkbox type="checkbox" checked={mudaPiel} disabled={modoAutomatico} onChange={() => setMudaPiel(!mudaPiel)} />
            <span>Muda de Piel</span>
          </Card>
        </CenterColumn>

        <RightColumn>
          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p>
              <StatusImage src="/icons/sun.png" /> Ciclo del Día: {cicloDia}
            </p>
            <StatusItem color="green">
              <StatusImage src="/icons/check.png" /> Temperatura: Todo en orden
            </StatusItem>
          </StatusPanel>
        </RightColumn>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
