import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Estilos Generales
const Container = styled.div`
  padding: 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled(Column)`
  justify-content: space-between;
`;

const RightColumn = styled(Column)`
  justify-content: space-between;
  height: 100%;
`;

// 📌 Botones de Modo de Control
const ButtonGroup = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 15px;
`;

const ModeButton = styled.button`
  background: ${({ active }) => (active ? "#093609" : "#E0E2E6")};
  color: ${({ active }) => (active ? "white" : "black")};
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  padding: 10px 15px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    background: #093609;
    color: white;
  }
`;

// 📌 Tarjetas con títulos sobresalientes
const Card = styled.div`
  background: rgba(123, 95, 61, 0.76);
  padding: 20px;
  border-radius: 15px;
  box-shadow: 4px 4px 8px rgba(0, 0, 0, 0.2);
  text-align: center;
  position: relative;
  height: 100%;
`;

const CardTitle = styled.h3`
  background: #093609;
  color: white;
  padding: 12px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  text-align: center;
`;

// 📌 Panel de Control
const ControlPanel = styled(Card)`
  padding-top: 40px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const DisabledOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  display: ${({ disabled }) => (disabled ? "block" : "none")};
`;

// 📌 Switches
const SwitchContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 80%;
  margin-top: 10px;
`;

const Switch = styled.label`
  display: flex;
  align-items: center;
  gap: 10px;
  cursor: pointer;
`;

const Checkbox = styled.input`
  transform: scale(1.5);
`;

// 📌 Estado General
const StatusPanel = styled(Card)``;

const StatusItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 18px;
  font-weight: bold;
  background: ${({ color }) => color || "gray"};
  padding: 10px;
  border-radius: 10px;
  margin-top: 10px;
`;

// 📌 Slider Personalizado
const SliderContainer = styled.div`
  width: 100%;
  height: 10px;
  background: rgba(255, 255, 255, 0.28);
  border-radius: 1000px;
  position: relative;
  display: flex;
  align-items: center;
  cursor: pointer;
`;

const ProgressBar = styled.div`
  height: 100%;
  background: #4caf50;
  border-radius: 1000px;
  width: ${({ value }) => value}%;
  transition: width 0.2s ease-in-out;
`;

const SliderCircle = styled.div`
  width: 20px;
  height: 20px;
  background: white;
  border: 2px solid #4caf50;
  border-radius: 50%;
  position: absolute;
  left: ${({ value }) => `calc(${value}% - 10px)`};
  top: 50%;
  transform: translateY(-50%);
  transition: left 0.2s ease-in-out;
  cursor: grab;
`;

const CustomSlider = ({ value, onChange }) => {
  const handleDrag = (e) => {
    const rect = e.target.parentElement.getBoundingClientRect();
    const newValue = Math.min(
      Math.max(((e.clientX - rect.left) / rect.width) * 100, 0),
      100
    );
    onChange(newValue);
  };

  return (
    <SliderContainer onMouseDown={(e) => handleDrag(e)}>
      <ProgressBar value={value} />
      <SliderCircle value={value} />
    </SliderContainer>
  );
};

// 📌 Dashboard Principal
const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(50);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(true);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaPiel, setMudaPiel] = useState(false);
  const [cicloDia, setCicloDia] = useState("mañana");

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        <Column>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <p>🌡️ 29°C - 32°C</p>
          </Card>
          <Card>
            <CardTitle>Iluminación</CardTitle>
            <p>☀️ Intensidad UV: 0.3</p>
          </Card>
          <Card>
            <CardTitle>Humedad</CardTitle>
            <p>💧 30%</p>
          </Card>
        </Column>

        <CenterColumn>
          <CustomSlider value={placaTermica} onChange={setPlacaTermica} />
        </CenterColumn>

        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <ButtonGroup>
              <ModeButton active={!modoAutomatico} onClick={() => setModoAutomatico(false)}>
                Modo Manual
              </ModeButton>
              <ModeButton active={modoAutomatico} onClick={() => setModoAutomatico(true)}>
                Modo Automático
              </ModeButton>
            </ButtonGroup>
          </ControlPanel>
        </RightColumn>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
