import React, { useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar los componentes de Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Estilos
const Container = styled.div`
  padding-top: 80px;
  padding: 40px;
  background: white;
  border-radius: 20px;
  width: 91%;
  max-width: 1200px;
  margin: auto;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  gap: 20px;
  height: 85vh;
`;

// 📌 Columnas principales
const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const LeftColumn = styled(Column)`width: 25%; align-items: center;`;
const CenterColumn = styled(Column)`width: 50%; align-items: center;`;
const RightColumn = styled(Column)`width: 25%; height: 100%; justify-content: space-between;`;

// 📌 Tarjetas
const Card = styled.div`
  background: #a77e4f;
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  width: 100%;
`;

const CardTitle = styled.h3`
  background: #216c2a;
  padding: 10px;
  border-radius: 10px;
  margin-bottom: 15px;
`;

const ControlPanel = styled(Card)`background: #5d4037; flex: 1;`;
const StatusPanel = styled(Card)`background: #3e2723; flex: 1;`;

const Info = styled.p`font-size: 20px; font-weight: bold; margin: 10px 0;`;
const ControlButton = styled.button`
  background: #4CAF50;
  border: none;
  color: white;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
  border-radius: 5px;
`;

const Dashboard = () => {
  const navigate = useNavigate();

  // 📌 **Verificar si el usuario está autenticado**
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login"); // Redirige al login si no hay token
    }
  }, [navigate]);

  // 📌 Datos de las gráficas
  const temperatureData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Temperatura", data: [27, 28, 29, 30, 29, 28], borderColor: "orange", backgroundColor: "rgba(255, 165, 0, 0.5)" }]
  };

  const illuminationData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Intensidad UV", data: [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], borderColor: "yellow", backgroundColor: "rgba(255, 255, 0, 0.5)" }]
  };

  const humidityData = {
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label: "Humedad %", data: [30, 35, 40, 38, 32, 30], borderColor: "blue", backgroundColor: "rgba(0, 0, 255, 0.5)" }]
  };

  return (
    <>
      <Header />
      <Container>
        <LeftColumn>
          <Card><CardTitle>Temperatura</CardTitle><Info>Mínima: 29°C - Máxima: 32°C</Info></Card>
          <Card><CardTitle>Iluminación</CardTitle><Info>Intensidad UV: 0.3</Info></Card>
          <Card><CardTitle>Humedad</CardTitle><Info>Humedad: 30%</Info></Card>
        </LeftColumn>

        <CenterColumn>
          <Card><CardTitle>Temperatura</CardTitle><Line data={temperatureData} /></Card>
          <Card><CardTitle>Iluminación</CardTitle><Line data={illuminationData} /></Card>
          <Card><CardTitle>Humedad</CardTitle><Line data={humidityData} /></Card>
        </CenterColumn>

        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <p>Modo Manual | Modo Automático</p>
            <p>Placa Térmica: <input type="range" min="0" max="100" /></p>
            <p>Lámparas: <button>ON</button> <button>OFF</button></p>
            <p>Humidificador: <ControlButton>ACTIVAR</ControlButton></p>
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p><strong>Ciclo del Día:</strong></p>
            <p>✅ Temperatura: Todo en orden</p>
            <p>⚠️ Iluminación: Necesita ajuste</p>
            <p>❌ Humedad: Necesita atención</p>
          </StatusPanel>
        </RightColumn>
      </Container>

      <Footer />
    </>
  );
};

export default Dashboard;
