import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// 📌 Estilos Generales
const Container = styled.div`
  padding: 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

// 📌 Tarjetas con títulos sobresalientes
const Card = styled.div`
  background: #a67c52;
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  height: 100%;
`;

const CardTitle = styled.h3`
  background: #3c722b;
  padding: 12px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  text-align: center;
`;

// 📌 Columnas y Alturas Uniformes
const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled(Column)`
  justify-content: space-between;
`;

const RightColumn = styled(Column)`
  justify-content: space-between;
  height: 100%;
`;

// 📌 Panel de Control con Diseño Exacto
const ControlPanel = styled(Card)`
  background: #5d4037;
  padding-top: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const DisabledOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  display: ${({ disabled }) => (disabled ? "block" : "none")};
`;

// 📌 Slider de Placa Térmica
const Slider = styled.input`
  width: 80%;
  margin: 10px 0;
`;

// 📌 Interruptores y Botón
const Switch = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 10px 0;
`;

const ControlButton = styled.button`
  background: #4caf50;
  border: none;
  color: white;
  padding: 10px;
  font-size: 14px;
  cursor: pointer;
  border-radius: 8px;
`;

// 📌 Contenedor de Gráficas (Mantener la misma altura)
const ChartContainer = styled.div`
  background: white;
  padding: 15px;
  border-radius: 15px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  height: 250px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

// 📌 Estado General
const StatusPanel = styled(Card)`
  background: #3e2723;
  height: 100%;
`;

const StatusItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  font-size: 18px;
  font-weight: bold;
  background: ${({ color }) => color || "gray"};
  padding: 10px;
  border-radius: 10px;
  margin-top: 10px;
`;

const Dashboard = () => {
  const navigate = useNavigate();
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [cicloDia, setCicloDia] = useState("mañana");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }

    const hora = new Date().getHours();
    if (hora >= 6 && hora < 12) setCicloDia("mañana");
    else if (hora >= 12 && hora < 18) setCicloDia("tarde");
    else setCicloDia("noche");
  }, [navigate]);

  // 📌 Datos de gráficas
  const chartData = (label, data, color) => ({
    labels: ["6 AM", "9 AM", "12 PM", "3 PM", "6 PM", "9 PM"],
    datasets: [{ label, data, borderColor: color, backgroundColor: `${color}50` }],
  });

  return (
    <>
      <Header showUserIcon={true} />
      <Container>
        {/* 📌 Columna Izquierda */}
        <Column>
          <Card>
            <CardTitle>Temperatura</CardTitle>
            <p>🌡️ 29°C - 32°C</p>
          </Card>
          <Card>
            <CardTitle>Iluminación</CardTitle>
            <p>☀️ Intensidad UV: 0.3</p>
          </Card>
          <Card>
            <CardTitle>Humedad</CardTitle>
            <p>💧 30%</p>
          </Card>
        </Column>

        {/* 📌 Columna Central */}
        <CenterColumn>
          <ChartContainer>
            <Line data={chartData("Temperatura", [27, 28, 29, 30, 29, 28], "#FF9800")} />
          </ChartContainer>
          <ChartContainer>
            <Line data={chartData("Intensidad UV", [0.2, 0.3, 0.4, 0.3, 0.2, 0.1], "#FFC107")} />
          </ChartContainer>
          <ChartContainer>
            <Line data={chartData("Humedad %", [30, 35, 40, 38, 32, 30], "#2196F3")} />
          </ChartContainer>
        </CenterColumn>

        {/* 📌 Columna Derecha */}
        <RightColumn>
          <ControlPanel>
            <CardTitle>Panel de Control</CardTitle>
            <p>Modo Manual | 🔄 Modo Automático</p>
            <Slider type="range" min="0" max="100" />
            <Switch>
              <span>💡 Visible</span>
              <input type="checkbox" />
              <span>UV</span>
              <input type="checkbox" />
            </Switch>
            <ControlButton>ACTIVAR</ControlButton>
            <DisabledOverlay disabled={modoAutomatico} />
          </ControlPanel>

          <StatusPanel>
            <CardTitle>Estado General</CardTitle>
            <p>🌞 Ciclo del Día: {cicloDia}</p>
            <StatusItem color="green">✅ Temperatura: Todo en orden</StatusItem>
            <StatusItem color="yellow">⚠️ Iluminación: Necesita ajuste</StatusItem>
            <StatusItem color="red">❌ Humedad: Necesita atención</StatusItem>
          </StatusPanel>
        </RightColumn>
      </Container>
      <Footer />
    </>
  );
};

export default Dashboard;
