import React, { useRef, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";

const NavBar = styled.nav`
  background-color: #B4864D;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 20px;
  position: fixed;
  width: 100%;
  top: 0;
  z-index: 100;
`;

const NavContent = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  max-width: 1200px;  /* Evita que el contenido se expanda demasiado */
  margin: auto; /* Centra el navbar */
`;

const LogoImg = styled.img`
  width: 50px;
`;

const Menu = styled.ul`
  display: flex;
  list-style: none;
  gap: 20px;
  flex-grow: 1;  /* Hace que el menú ocupe todo el espacio posible */
  justify-content: center; /* Centra las opciones del menú */
  margin: 0;
  padding: 0;
`;

const MenuItem = styled.li`
  font-weight: bold;
  cursor: pointer;
  color: white;

  &:hover {
    text-decoration: underline;
  }
`;

const LoginButton = styled.button`
  padding: 10px 15px;
  background-color: white;
  color: black;
  border: none;
  border-radius: 5px;
  font-weight: bold;
  cursor: pointer;
  transition: background 0.3s ease-in-out;
  white-space: nowrap; /* Evita que el texto del botón se corte */

  &:hover {
    background-color: #f5f5f5;
  }
`;

const Header = ({ setHeaderHeight = () => {}, hideLoginButton = false }) => {
    const headerRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        if (headerRef.current) {
            setHeaderHeight(headerRef.current.offsetHeight);
        }
    }, [setHeaderHeight]);

    return (
        <NavBar ref={headerRef}>
            <NavContent>
                <LogoImg src={logo} alt="Logo" />
                <Menu>
                    <MenuItem onClick={() => navigate("/")}>Inicio</MenuItem>
                    <MenuItem onClick={() => navigate("/dashboard")}>Centro de monitoreo y control</MenuItem>
                    <MenuItem onClick={() => navigate("/historial")}>Historial de datos</MenuItem>
                </Menu>
                {!hideLoginButton && (
                    <LoginButton onClick={() => navigate("/login")}>
                        Iniciar sesión
                    </LoginButton>
                )}
            </NavContent>
        </NavBar>
    );
};

export default Header;
