import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Registrar Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// üìå Estilos Generales
const Container = styled.div`
  padding: 30px;
  margin: auto;
  max-width: 1400px;
  background: #f8f4e1;
  border-radius: 20px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  gap: 20px;
  min-height: 85vh;
`;

const Column = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const CenterColumn = styled(Column)`
  justify-content: space-between;
`;

const RightColumn = styled(Column)`
  justify-content: space-between;
  height: 100%;
`;

// üìå Tarjetas de Informaci√≥n
const Card = styled.div`
  background: #7B5F3D;
  color: white;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  box-shadow: 4px 4px 8px rgba(0, 0, 0, 0.2);
  position: relative;
  height: 100%;
`;

const CardTitle = styled.h3`
  background: #093609;
  color: white;
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  padding: 12px;
  border-radius: 10px;
  font-size: 18px;
  position: absolute;
  top: -18px;
  left: 50%;
  transform: translateX(-50%);
  width: 80%;
  text-align: center;
`;

// üìå Panel de Control con M√°scara en Autom√°tico
const ControlPanel = styled(Card)`
  background: #5d4037;
  padding-top: 40px;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const DisabledOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  border-radius: 15px;
  display: ${({ disabled }) => (disabled ? "block" : "none")};
`;

// üìå Botones de Modo de Control
const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 15px;
`;

const ModeButton = styled.button`
  background: ${({ active }) => (active ? "#093609" : "#E0E2E6")};
  color: ${({ active }) => (active ? "white" : "black")};
  font-family: "Roboto", sans-serif;
  font-weight: bold;
  padding: 10px 15px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  &:hover {
    background: #093609;
    color: white;
  }
`;

// üìå Slider de Placa T√©rmica
const Slider = styled.input`
  width: 80%;
  margin: 10px 0;
`;

// üìå Switch para L√°mparas
const SwitchContainer = styled.div`
  display: flex;
  justify-content: space-between;
  width: 80%;
`;

const Switch = styled.label`
  display: flex;
  align-items: center;
  gap: 10px;
  cursor: pointer;
`;

const Checkbox = styled.input`
  transform: scale(1.5);
`;

// üìå Bot√≥n de Humidificador
const ControlButton = styled.button`
  background: #4caf50;
  border: none;
  color: white;
  padding: 12px;
  font-size: 16px;
  cursor: pointer;
  border-radius: 8px;
  margin-top: 10px;
`;

// üìå Contenedor de Gr√°ficas
const ChartContainer = styled.div`
  background: white;
  padding: 15px;
  border-radius: 15px;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.1);
  height: 250px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Dashboard = () => {
  const [modoAutomatico, setModoAutomatico] = useState(false);
  const [placaTermica, setPlacaTermica] = useState(85);
  const [luzVisible, setLuzVisible] = useState(false);
  const [luzUV, setLuzUV] = useState(true);
  const [humidificador, setHumidificador] = useState(false);
  const [mudaPiel, setMudaPiel] = useState(false);

  return (
    <Container>
      {/* üìå Columna Izquierda */}
      <Column>
        <Card>
          <CardTitle>Temperatura</CardTitle>
          <p>üå°Ô∏è 29¬∞C - 32¬∞C</p>
        </Card>
        <Card>
          <CardTitle>Iluminaci√≥n</CardTitle>
          <p>‚òÄÔ∏è Intensidad UV: 0.3</p>
        </Card>
        <Card>
          <CardTitle>Humedad</CardTitle>
          <p>üíß 30%</p>
        </Card>
      </Column>

      {/* üìå Columna Central (Gr√°ficas) */}
      <CenterColumn>
        <ChartContainer>
          <Line />
        </ChartContainer>
        <ChartContainer>
          <Line />
        </ChartContainer>
        <ChartContainer>
          <Line />
        </ChartContainer>
      </CenterColumn>

      {/* üìå Columna Derecha */}
      <RightColumn>
        <ControlPanel>
          <CardTitle>Panel de Control</CardTitle>
          <ButtonGroup>
            <ModeButton active={!modoAutomatico} onClick={() => setModoAutomatico(false)}>
              Modo Manual
            </ModeButton>
            <ModeButton active={modoAutomatico} onClick={() => setModoAutomatico(true)}>
              Modo Autom√°tico
            </ModeButton>
          </ButtonGroup>

          <Slider type="range" min="0" max="100" value={placaTermica} disabled={modoAutomatico} />

          <SwitchContainer>
            <Switch>
              <span>üí° Visible</span>
              <Checkbox type="checkbox" checked={luzVisible} disabled={modoAutomatico} />
            </Switch>
            <Switch>
              <span>UV</span>
              <Checkbox type="checkbox" checked={luzUV} disabled={modoAutomatico} />
            </Switch>
          </SwitchContainer>

          <ControlButton disabled={modoAutomatico}>ACTIVAR</ControlButton>

          <Switch>
            <span>Muda de Piel</span>
            <Checkbox type="checkbox" checked={mudaPiel} disabled={modoAutomatico} />
          </Switch>

          <DisabledOverlay disabled={modoAutomatico} />
        </ControlPanel>
      </RightColumn>
    </Container>
  );
};

export default Dashboard;
