};

// Función para preparar los datos del gráfico
const prepareChartData = (data, category) => {
  const numericalData = data.map(item => {
    // Extraer el valor numérico del dato
    let value = parseFloat(item.dato.replace(/[^\d.-]/g, ''));
    return {
      fecha: item.fecha,
      valor: value
    };
  });

  const chartData = {
    labels: numericalData.map(item => item.fecha),
    datasets: [
      {
        label: category,
        data: numericalData.map(item => item.valor),
        fill: false,
        backgroundColor: '#FF8C00',
        borderColor: '#FF8C00',
        tension: 0.4,
      }
    ]
  };
  
  return chartData;
};

const chartOptions = {
  responsive: true,
  maintainAspectRatio: false,
  plugins: {
    legend: {
      display: false
    },
    tooltip: {
      mode: 'index',
      intersect: false,
    }
  },
  scales: {
    x: {
      display: false,
    },
    y: {
      display: false,
    }
  },
  elements: {
    point: {
      radius: 0,
    },
  }
};

function Historial() {
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [filteredData, setFilteredData] = useState(initialData[selectedCategory]);
  const [chartData, setChartData] = useState(null);
  const [currentValue, setCurrentValue] = useState('');
  
  // Unidades según la categoría seleccionada
  const unidades = {
    'Temperatura': '°C',
    'Humedad': '%',
    'Iluminacion': 'lux'
  };

  // Efecto para actualizar los datos cuando cambia la categoría
  useEffect(() => {
    setFilteredData(initialData[selectedCategory]);
    setChartData(prepareChartData(initialData[selectedCategory], selectedCategory));
    
    // Obtener el valor más reciente para mostrar
    const lastItem = initialData[selectedCategory][initialData[selectedCategory].length - 1];
    setCurrentValue(lastItem.dato);
  }, [selectedCategory]);

  // Función para manejar el cambio de categoría
  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  return (
    <>
      <Header showUserIcon={true} />
      
      <Container>
        <ControlsRow>
          <CategorySelect onChange={handleCategoryChange} value={selectedCategory}>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </CategorySelect>
          
          <DownloadButton>
            <ArrowDown size={24} />
          </DownloadButton>
        </ControlsRow>
        
        <ContentRow>
          <DataPanel>
            <Table>
              <thead>
                <tr>
                  <Th>Fecha</Th>
                  <Th>Dato</Th>
                </tr>
              </thead>
              <tbody>
                {filteredData.map((item, index) => (
                  <tr key={index}>
                    <Td>{item.fecha}</Td>
                    <Td>{item.dato}</Td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </DataPanel>
          
          <DataPanel>
            <ChartContainer>
              <ChartTitle>{selectedCategory}</ChartTitle>
              <CurrentValue>{currentValue}</CurrentValue>
              <div style={{ height: '180px', maxWidth: '100%' }}>
                {chartData && <Line data={chartData} options={chartOptions} />}
              </div>
            </ChartContainer>
          </DataPanel>
        </ContentRow>
      </Container>
      
      <Footer />
    </>
  );
}

export default Historial;
