import React, { useRef, useEffect, useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";
import notificationIcon from "../assets/campana.png";
import userIcon from "../assets/usuario.png";
import { jwtDecode } from "jwt-decode";
// Importamos axios para hacer peticiones HTTP a nuestra API
import axios from "axios";


// Estilos
const NavBar = styled.nav`
  background-color: #B4864D;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 20px;
  position: fixed;
  width: 100%;
  top: 0;
  left: 0;
  z-index: 100;
  box-sizing: border-box;
`;

const LogoImg = styled.img`
  width: 50px;
  cursor: pointer;
`;

const NavContent = styled.div`
  display: flex;
  flex-grow: 1;
  justify-content: center;
  align-items: center;
`;

const Menu = styled.ul`
  display: flex;
  list-style: none;
  gap: 20px;
  margin: 0;
  padding: 0;
  
  @media (max-width: 768px) {
    gap: 15px;
  }
`;

const MenuItem = styled.li`
  font-weight: bold;
  cursor: pointer;
  color: white;
  
  &:hover {
    text-decoration: underline;
  }
`;

const RightSection = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  position: relative;
`;

const IconButton = styled.img`
  width: 40px;
  height: 40px;
  cursor: pointer;
  
  @media (max-width: 768px) {
    width: 30px;
    height: 30px;
  }
`;

const LoginButton = styled.button`
  padding: 10px 15px;
  background-color: white;
  color: black;
  border: none;
  border-radius: 5px;
  font-weight: bold;
  cursor: pointer;
  transition: background 0.3s ease-in-out;
  white-space: nowrap;
  
  &:hover {
    background-color: #f5f5f5;
  }
`;

const DropdownMenu = styled.div`
  position: absolute;
  top: 50px;
  right: 0;
  background-color: white;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0,0,0,0.3);
  overflow: hidden;
  z-index: 100;
`;

const DropdownMenuItem = styled.div`
  padding: 10px 15px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  
  &:hover {
    background-color: #f0f0f0;
  }
`;

// Nuevos componentes para las notificaciones
const NotificationDropdown = styled(DropdownMenu)`
  width: 300px;
  max-height: 400px;
  overflow-y: auto;
  right: ${props => props.position || "0"};
`;

const NotificationItem = styled.div`
  padding: 15px;
  border-bottom: 1px solid #eaeaea;
  cursor: pointer;
  transition: background-color 0.2s;
  
  &:hover {
    background-color: #f5f5f5;
  }
  
  &:last-child {
    border-bottom: none;
  }
`;

const NotificationTitle = styled.div`
  font-weight: bold;
  margin-bottom: 5px;
  display: flex;
  justify-content: space-between;
`;

const NotificationDescription = styled.div`
  font-size: 0.9rem;
  color: #555;
`;

const NotificationTime = styled.span`
  font-size: 0.8rem;
  color: #999;
`;

const UnreadIndicator = styled.div`
  width: 8px;
  height: 8px;
  background-color: #E74C3C;
  border-radius: 50%;
  margin-right: 10px;
  display: ${props => (props.read ? "none" : "block")};
`;

const NotificationBadge = styled.div`
  position: absolute;
  top: -5px;
  right: -5px;
  background-color: #E74C3C;
  color: white;
  border-radius: 50%;
  width: 18px;
  height: 18px;
  font-size: 12px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-weight: bold;
`;

const NotificationHeader = styled.div`
  padding: 15px;
  font-weight: bold;
  background-color: #f9f9f9;
  border-bottom: 1px solid #eaeaea;
  display: flex;
  justify-content: space-between;
`;

const NotificationIconContainer = styled.div`
  position: relative;
`;

const NoNotifications = styled.div`
  padding: 20px;
  text-align: center;
  color: #777;
`;

const UserName = styled.span`
  margin-right: 10px;
  font-size: 14px;
  font-weight: 500;
  color: #333;
`;

const Header = ({ setHeaderHeight = () => {} }) => {
  const headerRef = useRef(null);
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [menuVisible, setMenuVisible] = useState(false);
  const [notificationMenuVisible, setNotificationMenuVisible] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const token = localStorage.getItem("token");

  let username = '';
  let userId = '';

  if (token) {
    const decoded = jwtDecode(token); // Necesitas la librería jwt-decode
    username = decoded.user;
    userId = decoded.id;
  }

  // Función para formatear el tiempo transcurrido
  const formatTimeElapsed = (timeAlert) => {
    // Convertir el timeAlert a objeto Date si viene como string
    const alertTime = timeAlert instanceof Date ? timeAlert : new Date(timeAlert);
    const currentTime = new Date();
    
    // Calcular la diferencia en milisegundos
    const diffMs = currentTime - alertTime;
    
    // Convertir a diferentes unidades de tiempo
    const seconds = Math.floor(diffMs / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);
    const weeks = Math.floor(days / 7);
    const months = Math.floor(days / 30.44); // Promedio de días por mes
    const years = Math.floor(days / 365.25); // Considerando años bisiestos
    
    // Determinar el formato adecuado basado en el tiempo transcurrido
    if (seconds < 60) {
      return seconds <= 0 ? 'hace un momento' : `hace ${seconds} segundo${seconds !== 1 ? 's' : ''}`;
    } else if (minutes < 60) {
      return `hace ${minutes} minuto${minutes !== 1 ? 's' : ''}`;
    } else if (hours < 24) {
      return `hace ${hours} hora${hours !== 1 ? 's' : ''}`;
    } else if (days < 7) {
      return `hace ${days} día${days !== 1 ? 's' : ''}`;
    } else if (weeks < 4) {
      return `hace ${weeks} semana${weeks !== 1 ? 's' : ''}`;
    } else if (months < 12) {
      return `hace ${months} mes${months !== 1 ? 'es' : ''}`;
    } else {
      return `hace ${years} año${years !== 1 ? 's' : ''}`;
    }
  };

  // Actualizar el tiempo cada segundo si el menú de notificaciones está visible
  useEffect(() => {
    let intervalId;
    
    if (notificationMenuVisible && notifications.length > 0) {
      intervalId = setInterval(() => {
        // Forzar la re-renderización para actualizar los tiempos mostrados
        setNotifications([...notifications]);
      }, 1000);
    }

    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [notificationMenuVisible, notifications]);

  useEffect(() => {
    if (headerRef.current) {
      setHeaderHeight(headerRef.current.offsetHeight);
    }
    
    if (token) {
      setIsAuthenticated(true);
      
      // Usar la ruta correcta con el ID de usuario como parámetro
      axios.get(`http://localhost:5000/api/getNotificationsByUserId/${userId}`)
        .then((response) => {
          const data = response.data;
          if (data.notifications) {
            // Convertir id_notificacion a id para mantener compatibilidad
            const formattedNotifications = data.notifications.map(notif => ({
              id: notif.id_notificacion,
              tipo: notif.tipo,
              descripcion: notif.descripcion,
              time_alert: notif.time_alert,
              is_read: notif.is_read === 1 || notif.is_read === true
            }));
            
            setNotifications(formattedNotifications);
            setUnreadCount(formattedNotifications.filter(notif => !notif.is_read).length);
          }
        })
        .catch((error) => console.error('Error al obtener notificaciones:', error));
    } else {
      setIsAuthenticated(false);
    }
  }, [userId, token, setHeaderHeight]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    navigate("/login");
  };

  const handleNotificationClick = (notificationId) => {
    // Actualizar el estado de la notificación a leída
    const updatedNotifications = notifications.map(notif =>
      notif.id === notificationId ? { ...notif, is_read: true } : notif
    );
    
    setNotifications(updatedNotifications);
    
    // Actualizar el contador de notificaciones no leídas
    setUnreadCount(updatedNotifications.filter(notif => !notif.is_read).length);
    
    // Cerrar el menú de notificaciones
    setNotificationMenuVisible(false);
    
    // Aquí podríamos añadir una llamada a la API para actualizar el estado en el servidor
    // Por ejemplo: axios.post(`http://localhost:5000/api/markNotificationAsRead/${notificationId}`);
  };

  const markAllAsRead = () => {
    const updatedNotifications = notifications.map(notif => ({ ...notif, is_read: true }));
    setNotifications(updatedNotifications);
    setUnreadCount(0);
    
    // Aquí podríamos añadir una llamada a la API para actualizar el estado en el servidor
    // Por ejemplo: axios.post(`http://localhost:5000/api/markAllNotificationsAsRead/${userId}`);
  };

  // Función para manejar el clic en el icono de notificaciones
  const handleNotificationIconClick = () => {
    // Alterna la visibilidad del menú de notificaciones
    setNotificationMenuVisible(!notificationMenuVisible);
    setMenuVisible(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (notificationMenuVisible || menuVisible) {
        if (!event.target.closest('.dropdown-menu') && 
            !event.target.matches('[alt="Notificaciones"]') && 
            !event.target.matches('[alt="Usuario"]')) {
          setNotificationMenuVisible(false);
          setMenuVisible(false);
        }
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [notificationMenuVisible, menuVisible]);


  return (
    <NavBar ref={headerRef}>
      <LogoImg src={logo} alt="Logo" onClick={() => navigate("/")} />
      
      <NavContent>
        <Menu>
          <MenuItem onClick={() => navigate("/")}>Inicio</MenuItem>
          <MenuItem onClick={() => navigate("/dashboard")}>Centro de monitoreo y control</MenuItem>
          <MenuItem onClick={() => navigate("/historial")}>Historial de datos</MenuItem>
        </Menu>
      </NavContent>
      
      <RightSection>
        {isAuthenticated ? (
          <>
            <NotificationIconContainer>
            <UserName>{username}</UserName>
              <IconButton 
                src={notificationIcon} 
                alt="Notificaciones" 
                onClick={handleNotificationIconClick}
              />
              {unreadCount > 0 && <NotificationBadge>{unreadCount}</NotificationBadge>}
              
              {notificationMenuVisible && (
                <NotificationDropdown className="dropdown-menu" position="60px">
                  <NotificationHeader>
                    Notificaciones
                    {unreadCount > 0 && (
                      <span 
                        style={{ fontSize: '0.8rem', color: '#5a5a5a', cursor: 'pointer' }}
                        onClick={markAllAsRead}
                      >
                        Marcar todas como leídas
                      </span>
                    )}
                  </NotificationHeader>
                  
                  {notifications.length > 0 ? (
                    notifications.map(notification => (
                      <NotificationItem 
                        key={notification.id}
                        onClick={() => handleNotificationClick(notification.id)}
                      >
                        <NotificationTitle>
                          <div style={{ display: 'flex', alignItems: 'center' }}>
                            <UnreadIndicator read={notification.is_read} />
                            {notification.tipo}
                          </div>
                          <NotificationTime>
                            {formatTimeElapsed(notification.time_alert)}
                          </NotificationTime>
                        </NotificationTitle>
                        <NotificationDescription>
                          {notification.descripcion}
                        </NotificationDescription>
                      </NotificationItem>
                    ))
                  ) : (
                    <NoNotifications>No tienes notificaciones</NoNotifications>
                  )}
                </NotificationDropdown>
              )}
            </NotificationIconContainer>
            
            <IconButton 
              src={userIcon} 
              alt="Usuario" 
              onClick={() => {
                setMenuVisible(!menuVisible);
                setNotificationMenuVisible(false);
              }}
            />
            
            {menuVisible && (
              <DropdownMenu className="dropdown-menu">
                <DropdownMenuItem onClick={() => navigate("/editar-datos")}>Editar datos</DropdownMenuItem>
                <DropdownMenuItem onClick={handleLogout}>Cerrar sesión</DropdownMenuItem>
              </DropdownMenu>
            )}
          </>
        ) : (
          <LoginButton onClick={() => navigate("/login")}>Iniciar sesión</LoginButton>
        )}
      </RightSection>
    </NavBar>
  );
};

export default Header;
